                    .search-icon {
                        position: absolute;
                        left: 0.75rem;
                        top: 50%;
                        transform: translateY(-50%);
                        color: #667eea;
                        font-size: 1.1rem;
                    }

                    .stats-bar {
                        background: rgba(255, 255, 255, 0.7);
                        backdrop-filter: blur(10px);
                        border-radius: 15px;
                        padding: 1rem;
                        margin-bottom: 2rem;
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        border: 1px solid rgba(255, 255, 255, 0.3);
                    }

                    .pagination-enhanced {
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        gap: 1rem;
                        margin-top: 3rem;
                        padding: 2rem;
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(15px);
                        border-radius: 20px;
                        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
                    }

                    .page-info {
                        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                        -webkit-background-clip: text;
                        -webkit-text-fill-color: transparent;
                        background-clip: text;
                        font-weight: 600;
                        font-size: 1.1rem;
                    }

                    .grid-container {
                        display: grid;
                        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
                        gap: 2rem;
                        margin-bottom: 2rem;
                    }

                    .list-container {
                        display: flex;
                        flex-direction: column;
                        gap: 1.5rem;
                    }

                    .empty-state {
                        text-align: center;
                        padding: 4rem 2rem;
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(15px);
                        border-radius: 20px;
                        margin: 2rem 0;
                    }

                    .empty-state h3 {
                        color: #667eea;
                        margin-bottom: 1rem;
                    }

                    @media (max-width: 768px) {
                        .news-controls {
                            flex-direction: column;
                            align-items: stretch;
                        }

                        .control-group {
                            justify-content: center;
                        }

                        .search-box {
                            max-width: 100%;
                        }

                        .grid-container {
                            grid-template-columns: 1fr;
                            gap: 1rem;
                        }

                        .news-title {
                            font-size: 2rem;
                        }
                    }
                `}</style>

                <div className="news-container">
                    <div className="container">
                        {/* Enhanced Header */}
                        <div className="news-header">
                            <h1 className="news-title">
                                📰 {this.getCategoryDisplayName()} News - Latest Updates
                            </h1>
                            <p className="text-center text-muted mb-0">
                                Stay informed with the latest {this.getCategoryDisplayName().toLowerCase()} news from around the world
                            </p>
                        </div>

                        {/* Enhanced Controls */}
                        <div className="news-controls">
                            <div className="control-group">
                                <button 
                                    className={`enhanced-btn ${this.state.viewMode === 'grid' ? '' : 'outline'}`}
                                    onClick={this.toggleViewMode}
                                >
                                    {this.state.viewMode === 'grid' ? '📊 Grid View' : '📋 List View'}
                                </button>

                                <select 
                                    className="form-select"
                                    style={{
                                        borderRadius: '25px',
                                        border: '2px solid rgba(102, 126, 234, 0.2)',
                                        background: 'rgba(255, 255, 255, 0.9)',
                                        backdropFilter: 'blur(10px)'
                                    }}
                                    value={this.state.sortBy}
                                    onChange={(e) => this.handleSort(e.target.value)}
                                >
                                    <option value="publishedAt">📅 Latest First</option>
                                    <option value="popularity">🔥 Most Popular</option>
                                    <option value="relevancy">🎯 Most Relevant</option>
                                </select>
                            </div>

                            <div className="search-box">
                                <span className="search-icon">🔍</span>
                                <input
                                    type="text"
                                    className="search-input"
                                    placeholder="Search news..."
                                    value={this.state.searchQuery}
                                    onChange={(e) => this.handleSearch(e.target.value)}
                                />
                            </div>
                        </div>

                        {/* Stats Bar */}
                        <div className="stats-bar">
                            <div>
                                <strong>📊 Total Articles:</strong> {this.state.totalResults}
                            </div>
                            <div>
                                <strong>📄 Showing:</strong> {filteredArticles.length} articles
                            </div>
                            <div>
                                <strong>📖 Page:</strong> {this.state.page_no} of {totalPages}
                            </div>
                        </div>

                        {/* Loading Spinner */}
                        {this.state.loading && (
                            <div className="text-center py-5">
                                <Spinner/>
                            </div>
                        )}

                        {/* News Grid/List */}
                        {!this.state.loading && (
                            <>
                                {filteredArticles.length > 0 ? (
                                    <div className={this.state.viewMode === 'grid' ? 'grid-container' : 'list-container'}>
                                        {filteredArticles.map((element, index) => {
                                            return element.urlToImage && (
                                                <div key={element.url || index}>
                                                    <NewsItem 
                                                        title={element.title}  
                                                        desc={element.description} 
                                                        urlToImage={element.urlToImage} 
                                                        url={element.url}
                                                        author={element.author}
                                                        publishedAt={element.publishedAt}
                                                        source={element.source.name}
                                                        viewMode={this.state.viewMode}
                                                    />
                                                </div>
                                            )
                                        })}
                                    </div>
                                ) : (
                                    <div className="empty-state">
                                        <h3>🔍 No articles found</h3>
                                        <p>Try adjusting your search terms or check back later for new content.</p>
                                    </div>
                                )}
                            </>
                        )}

                        {/* Enhanced Pagination */}
                        {!this.state.loading && this.state.totalResults > 0 && (
                            <div className="pagination-enhanced">
                                <button 
                                    type="button" 
                                    disabled={this.state.page_no <= 1} 
                                    onClick={this.handleprev} 
                                    className="enhanced-btn"
                                >
                                    ⬅️ Previous
                                </button>

                                <div className="page-info">
                                    Page {this.state.page_no} of {totalPages}
                                </div>

                                <button 
                                    type="button" 
                                    disabled={this.state.page_no >= totalPages} 
                                    onClick={this.handlenext} 
                                    className="enhanced-btn"
                                >
                                    Next ➡️
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            </>
        )
    }
}

export default News
