import React, { useState, useEffect } from 'react';
import { ChevronLeft, Send, Image, Calendar, MapPin, User, FileText, Tag, CheckCircle, AlertCircle, Newspaper } from 'lucide-react';

const PostNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    author: '',
    category: '',
    location: '',
    imageUrl: '',
    tags: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [localNews, setLocalNews] = useState([]);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    setLocalNews([]);
  }, []);

  const categories = [
    'Breaking News',
    'Politics', 
    'Sports',
    'Entertainment',
    'Technology',
    'Health',
    'Business',
    'Education',
    'Weather',
    'Community',
    'Crime',
    'Traffic'
  ];

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    if (!formData.content.trim()) {
      newErrors.content = 'Content is required';
    }
    if (!formData.author.trim()) {
      newErrors.author = 'Author name is required';
    }
    if (!formData.category) {
      newErrors.category = 'Category is required';
    }
    if (formData.imageUrl && !isValidUrl(formData.imageUrl)) {
      newErrors.imageUrl = 'Please enter a valid URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const newsArticle = {
        id: Date.now().toString(),
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        publishedAt: new Date().toISOString(),
        source: 'Local News',
        isLocal: true
      };

      const updatedNews = [newsArticle, ...localNews];
      setLocalNews(updatedNews);
      setSubmitSuccess(true);

      setFormData({
        title: '',
        content: '',
        author: '',
        category: '',
        location: '',
        imageUrl: '',
        tags: ''
      });

      setTimeout(() => setSubmitSuccess(false), 4000);

    } catch (error) {
      console.error('Error saving news:', error);
      alert('Error saving news. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <>
      <style>{`
        .post-news-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
        }

        .header {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(10px);
          border-bottom: 1px solid rgba(255, 255, 255, 0.2);
          position: sticky;
          top: 0;
          z-index: 100;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
        }

        .header-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 1rem 2rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 70px;
        }

        .header-left {
          display: flex;
          align-items: center;
          gap: 1rem;
        }

        .back-button {
          padding: 0.75rem;
          background: rgba(102, 126, 234, 0.1);
          border: none;
          border-radius: 12px;
          cursor: pointer;
          transition: all 0.3s ease;
          color: #667eea;
        }

        .back-button:hover {
          background: rgba(102, 126, 234, 0.2);
          transform: translateY(-2px);
        }

        .header-title {
          display: flex;
          align-items: center;
          gap: 0.75rem;
        }

        .header-title h1 {
          font-size: 1.5rem;
          font-weight: 700;
          color: #2d3748;
          margin: 0;
        }

        .header-subtitle {
          font-size: 0.9rem;
          color: #718096;
          margin: 0;
        }

        .main-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: calc(70px + 2rem) 2rem 2rem 2rem;
          display: grid;
          grid-template-columns: 1fr 350px;
          gap: 2rem;
          align-items: start;
        }

        @media (max-width: 1024px) {
          .main-content {
            grid-template-columns: 1fr;
            gap: 1.5rem;
          }
        }

        .form-card {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(20px);
          border-radius: 20px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          border: 1px solid rgba(255, 255, 255, 0.2);
          overflow: hidden;
        }

        .form-header {
          padding: 2rem 2rem 1rem;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
        }

        .form-header h2 {
          font-size: 1.5rem;
          font-weight: 700;
          margin: 0 0 0.5rem 0;
        }

        .form-header p {
          opacity: 0.9;
          margin: 0;
        }

        .form-content {
          padding: 2rem;
        }

        .form-group {
          margin-bottom: 1.5rem;
        }

        .form-label {
          display: block;
          font-weight: 600;
          color: #2d3748;
          margin-bottom: 0.5rem;
          font-size: 0.9rem;
        }

        .form-input {
          width: 100%;
          padding: 1rem;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: rgba(255, 255, 255, 0.8);
          box-sizing: border-box;
        }

        .form-input:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          transform: translateY(-1px);
        }

        .form-input.error {
          border-color: #e53e3e;
        }

        .form-textarea {
          resize: vertical;
          min-height: 120px;
        }

        .form-select {
          width: 100%;
          padding: 1rem;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: rgba(255, 255, 255, 0.8);
          box-sizing: border-box;
        }

        .form-select:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .form-row {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 1rem;
        }

        @media (max-width: 768px) {
          .form-row {
            grid-template-columns: 1fr;
          }
        }

        .error-message {
          color: #e53e3e;
          font-size: 0.8rem;
          margin-top: 0.5rem;
          display: flex;
          align-items: center;
          gap: 0.25rem;
        }

        .submit-button {
          width: 100%;
          padding: 1rem 2rem;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          border: none;
          border-radius: 12px;
          font-size: 1.1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 0.5rem;
          margin-top: 1rem;
        }

        .submit-button:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 10px 25px rgba(102, 126, 234, 0.3);
        }

        .submit-button:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        .success-message {
          margin: 1.5rem 2rem 2rem;
          padding: 1rem;
          background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);
          color: white;
          border-radius: 12px;
          display: flex;
          align-items: center;
          gap: 0.5rem;
          animation: slideIn 0.3s ease;
        }

        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .sidebar {
          display: flex;
          flex-direction: column;
          gap: 1.5rem;
        }

        .sidebar-card {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(20px);
          border-radius: 20px;
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
          border: 1px solid rgba(255, 255, 255, 0.2);
          overflow: hidden;
        }

        .sidebar-header {
          padding: 1.5rem;
          background: linear-gradient(135deg, #4c51bf 0%, #553c9a 100%);
          color: white;
        }

        .sidebar-header h3 {
          font-size: 1.1rem;
          font-weight: 600;
          margin: 0;
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }

        .sidebar-content {
          padding: 1.5rem;
        }

        .guideline-item {
          display: flex;
          align-items: flex-start;
          gap: 0.75rem;
          margin-bottom: 1rem;
          padding: 1rem;
          background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
          border-radius: 12px;
          border-left: 4px solid #667eea;
        }

        .guideline-dot {
          width: 8px;
          height: 8px;
          border-radius: 50%;
          background: #667eea;
          margin-top: 6px;
          flex-shrink: 0;
        }

        .guideline-text h4 {
          font-weight: 600;
          color: #2d3748;
          margin: 0 0 0.25rem 0;
          font-size: 0.9rem;
        }

        .guideline-text p {
          color: #4a5568;
          margin: 0;
          font-size: 0.8rem;
          line-height: 1.4;
        }

        .news-item {
          padding: 1rem;
          background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
          border-radius: 12px;
          margin-bottom: 1rem;
          border: 1px solid rgba(102, 126, 234, 0.1);
          transition: all 0.3s ease;
        }

        .news-item:hover {
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(102, 126, 234, 0.2);
        }

        .news-title {
          font-weight: 600;
          color: #2d3748;
          margin: 0 0 0.5rem 0;
          font-size: 0.9rem;
          line-height: 1.3;
        }

        .news-meta {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          font-size: 0.75rem;
          color: #718096;
        }

        .category-tag {
          padding: 0.25rem 0.5rem;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          border-radius: 6px;
          font-size: 0.7rem;
          font-weight: 500;
        }

        .empty-state {
          text-align: center;
          padding: 2rem 1rem;
        }

        .empty-icon {
          width: 48px;
          height: 48px;
          background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 0 auto 1rem;
        }

        .empty-text {
          color: #718096;
          font-size: 0.9rem;
          margin: 0 0 0.5rem 0;
        }

        .empty-cta {
          color: #667eea;
          font-weight: 600;
          font-size: 0.9rem;
          margin: 0;
        }

        .spinner {
          width: 20px;
          height: 20px;
          border: 2px solid transparent;
          border-top: 2px solid currentColor;
          border-radius: 50%;
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          to {
            transform: rotate(360deg);
          }
        }

        .help-text {
          font-size: 0.8rem;
          color: #718096;
          margin-top: 0.5rem;
        }
      `}</style>

      <div className="post-news-container">
        {/* Header */}
        <div className="header">
          <div className="header-content">
            <div className="header-left">
              <button onClick={goBack} className="back-button">
                <ChevronLeft size={20} />
              </button>
              <div className="header-title">
                <Newspaper size={32} color="#667eea" />
                <div>
                  <h1>Post Local News</h1>
                  <p className="header-subtitle">Share breaking news from your community</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Form */}
          <div className="form-card">
            <div className="form-header">
              <h2>Create News Article</h2>
              <p>Fill in the details below to publish your news story</p>
            </div>

            <div className="form-content">
              {/* Title */}
              <div className="form-group">
                <label className="form-label">Article Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  placeholder="Enter compelling news headline..."
                  className={`form-input ${errors.title ? 'error' : ''}`}
                  required
                />
                {errors.title && (
                  <div className="error-message">
                    <AlertCircle size={16} />
                    {errors.title}
                  </div>
                )}
              </div>

              {/* Content */}
              <div className="form-group">
                <label className="form-label">Article Content *</label>
                <textarea
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                  placeholder="Write your news article content here... Include who, what, when, where, and why."
                  className={`form-input form-textarea ${errors.content ? 'error' : ''}`}
                  required
                />
                {errors.content && (
                  <div className="error-message">
                    <AlertCircle size={16} />
                    {errors.content}
                  </div>
                )}
              </div>

              {/* Author and Category */}
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Author Name *</label>
                  <input
                    type="text"
                    name="author"
                    value={formData.author}
                    onChange={handleInputChange}
                    placeholder="Your full name"
                    className={`form-input ${errors.author ? 'error' : ''}`}
                    required
                  />
                  {errors.author && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.author}
                    </div>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Category *</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    className={`form-select ${errors.category ? 'error' : ''}`}
                    required
                  >
                    <option value="">Select category</option>
                    {categories.map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                  {errors.category && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.category}
                    </div>
                  )}
                </div>
              </div>

              {/* Location and Image URL */}
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Location</label>
                  <input
                    type="text"
                    name="location"
                    value={formData.location}
                    onChange={handleInputChange}
                    placeholder="City, State/Country"
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Image URL</label>
                  <input
                    type="url"
                    name="imageUrl"
                    value={formData.imageUrl}
                    onChange={handleInputChange}
                    placeholder="https://example.com/image.jpg"
                    className={`form-input ${errors.imageUrl ? 'error' : ''}`}
                  />
                  {errors.imageUrl && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.imageUrl}
                    </div>
                  )}
                </div>
              </div>

              {/* Tags */}
              <div className="form-group">
                <label className="form-label">Tags</label>
                <input
                  type="text"
                  name="tags"
                  value={formData.tags}
                  onChange={handleInputChange}
                  placeholder="breaking, local, community, urgent"
                  className="form-input"
                />
                <p className="help-text">Separate tags with commas for better categorization</p>
              </div>

              <button
                type="button"
                onClick={handleSubmit}
                disabled={isSubmitting}
                className="submit-button"
              >
                {isSubmitting ? (
                  <>
                    <div className="spinner"></div>
                    Publishing Article...
                  </>
                ) : (
                  <>
                    <Send size={20} />
                    Publish News Article
                  </>
                )}
              </button>
            </div>

            {/* Success Message */}
            {submitSuccess && (
              <div className="success-message">
                <CheckCircle size={20} />
                <div>
                  <strong>Article Published Successfully!</strong>
                  <br />
                  Your local news has been added to the community feed.
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="sidebar">
            {/* Guidelines */}
            <div className="sidebar-card">
              <div className="sidebar-header">
                <h3>
                  <FileText size={20} />
                  Publishing Guidelines
                </h3>
              </div>
              <div className="sidebar-content">
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Clear Headlines</h4>
                    <p>Write compelling, accurate headlines that capture your story's essence</p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Complete Context</h4>
                    <p>Include who, what, when, where, and why in your content</p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Proper Categorization</h4>
                    <p>Choose appropriate categories and add relevant tags</p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Fact Verification</h4>
                    <p>Verify information and cite sources when possible</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Recent Local News */}
            <div className="sidebar-card">
              <div className="sidebar-header">
                <h3>
                  <Calendar size={20} />
                  Recent Local News
                </h3>
              </div>
              <div className="sidebar-content">
                {localNews.length > 0 ? (
                  <div>
                    {localNews.slice(0, 3).map((news) => (
                      <div key={news.id} className="news-item">
                        <h4 className="news-title">{news.title}</h4>
                        <div className="news-meta">
                          <span className="category-tag">{news.category}</span>
                          <span></span>
                          <span>{new Date(news.publishedAt).toLocaleDateString()}</span>
                        </div>
                        {news.author && (
                          <p style={{fontSize: '0.75rem', color: '#718096', margin: '0.5rem 0 0 0'}}>
                            By {news.author}
                          </p>
                        )}
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <div className="empty-icon">
                      <FileText size={24} color="#667eea" />
                    </div>
                    <p className="empty-text">No local news articles yet.</p>
                    <p className="empty-cta">Be the first to post!</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default PostNews;
