import React, { useState } from "react";
import {
  ChevronLeft,
  Send,
  Download,
  CheckCircle,
  AlertCircle,
  Newspaper,
  FileText,
} from "lucide-react";
import "./PostNews.css";

const PostNews = () => {
  const [formData, setFormData] = useState({
    title: "",
    content: "",
    author: "",
    category: "",
    location: "",
    imageUrl: "",
    tags: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [errors, setErrors] = useState({});

  const categories = [
    "Breaking News",
    "Politics",
    "Sports",
    "Entertainment",
    "Technology",
    "Health",
    "Business",
    "Education",
    "Weather",
    "Community",
    "Crime",
    "Traffic",
  ];

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = "Title is required";
    }
    if (!formData.content.trim()) {
      newErrors.content = "Content is required";
    }
    if (!formData.author.trim()) {
      newErrors.author = "Author name is required";
    }
    if (!formData.category) {
      newErrors.category = "Category is required";
    }
    if (formData.imageUrl && !isValidUrl(formData.imageUrl)) {
      newErrors.imageUrl = "Please enter a valid URL";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  // Function to save data as JSON file
  const saveToLocalFile = (newsArticle) => {
    try {
      // Create a blob with the news article data
      const dataStr = JSON.stringify(newsArticle, null, 2);
      const dataBlob = new Blob([dataStr], { type: "application/json" });

      // Create download link
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement("a");
      link.href = url;

      // Generate filename with timestamp
      const timestamp = new Date().toISOString().replace(/[:.]/g, "-");
      const filename = `news-article-${timestamp}.json`;
      link.download = filename;

      // Trigger download
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      // Clean up
      URL.revokeObjectURL(url);

      console.log("News article saved as:", filename);
    } catch (error) {
      console.error("Error saving file:", error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const newsArticle = {
        id: Date.now().toString(),
        ...formData,
        tags: formData.tags
          .split(",")
          .map((tag) => tag.trim())
          .filter((tag) => tag),
        publishedAt: new Date().toISOString(),
        source: "Local News",
        isLocal: true,
      };

      // Save to local file
      saveToLocalFile(newsArticle);

      setSubmitSuccess(true);

      // Reset form
      setFormData({
        title: "",
        content: "",
        author: "",
        category: "",
        location: "",
        imageUrl: "",
        tags: "",
      });

      setTimeout(() => setSubmitSuccess(false), 4000);
    } catch (error) {
      console.error("Error saving news:", error);
      alert("Error saving news. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <>
      <div className="post-news-container">
        {/* Header */}
        <div className="header">
          <div className="header-content">
            <div className="header-left">
              <button onClick={goBack} className="back-button">
                <ChevronLeft size={20} />
              </button>
              <div className="header-title">
                <Newspaper size={32} color="#667eea" />
                <div>
                  <h1>Post Local News</h1>
                  <p className="header-subtitle">
                    Share breaking news from your community
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Form */}
          <div className="form-card">
            <div className="form-header">
              <h2>Create News Article</h2>
              <p>Fill in the details below to publish your news story</p>
            </div>

            <div className="form-content">
              {/* Title */}
              <div className="form-group">
                <label className="form-label">Article Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  placeholder="Enter compelling news headline..."
                  className={`form-input ${errors.title ? "error" : ""}`}
                  required
                />
                {errors.title && (
                  <div className="error-message">
                    <AlertCircle size={16} />
                    {errors.title}
                  </div>
                )}
              </div>

              {/* Content */}
              <div className="form-group">
                <label className="form-label">Article Content *</label>
                <textarea
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                  placeholder="Write your news article content here... Include who, what, when, where, and why."
                  className={`form-input form-textarea ${
                    errors.content ? "error" : ""
                  }`}
                  required
                />
                {errors.content && (
                  <div className="error-message">
                    <AlertCircle size={16} />
                    {errors.content}
                  </div>
                )}
              </div>

              {/* Author and Category */}
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Author Name *</label>
                  <input
                    type="text"
                    name="author"
                    value={formData.author}
                    onChange={handleInputChange}
                    placeholder="Your full name"
                    className={`form-input ${errors.author ? "error" : ""}`}
                    required
                  />
                  {errors.author && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.author}
                    </div>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Category *</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    className={`form-select ${errors.category ? "error" : ""}`}
                    required
                  >
                    <option value="">Select category</option>
                    {categories.map((cat) => (
                      <option key={cat} value={cat}>
                        {cat}
                      </option>
                    ))}
                  </select>
                  {errors.category && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.category}
                    </div>
                  )}
                </div>
              </div>

              {/* Location and Image URL */}
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Location</label>
                  <input
                    type="text"
                    name="location"
                    value={formData.location}
                    onChange={handleInputChange}
                    placeholder="City, State/Country"
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Image URL</label>
                  <input
                    type="url"
                    name="imageUrl"
                    value={formData.imageUrl}
                    onChange={handleInputChange}
                    placeholder="https://example.com/image.jpg"
                    className={`form-input ${errors.imageUrl ? "error" : ""}`}
                  />
                  {errors.imageUrl && (
                    <div className="error-message">
                      <AlertCircle size={16} />
                      {errors.imageUrl}
                    </div>
                  )}
                </div>
              </div>

              {/* Tags */}
              <div className="form-group">
                <label className="form-label">Tags</label>
                <input
                  type="text"
                  name="tags"
                  value={formData.tags}
                  onChange={handleInputChange}
                  placeholder="breaking, local, community, urgent"
                  className="form-input"
                />
                <p className="help-text">
                  Separate tags with commas for better categorization
                </p>
              </div>

              <button
                type="button"
                onClick={handleSubmit}
                disabled={isSubmitting}
                className="submit-button"
              >
                {isSubmitting ? (
                  <>
                    <div className="spinner"></div>
                    Publishing Article...
                  </>
                ) : (
                  <>
                    <Download size={20} />
                    Save News Article
                  </>
                )}
              </button>
            </div>

            {/* Success Message */}
            {submitSuccess && (
              <div className="success-message">
                <CheckCircle size={20} />
                <div>
                  <strong>Article Saved Successfully!</strong>
                  <br />
                  Your news article has been downloaded as a JSON file.
                </div>
              </div>
            )}
          </div>

          {/* Sidebar - Only Guidelines */}
          <div className="sidebar">
            <div className="sidebar-card">
              <div className="sidebar-header">
                <h3>
                  <FileText size={20} />
                  Publishing Guidelines
                </h3>
              </div>
              <div className="sidebar-content">
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Clear Headlines</h4>
                    <p>
                      Write compelling, accurate headlines that capture your
                      story's essence
                    </p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Complete Context</h4>
                    <p>
                      Include who, what, when, where, and why in your content
                    </p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Proper Categorization</h4>
                    <p>Choose appropriate categories and add relevant tags</p>
                  </div>
                </div>
                <div className="guideline-item">
                  <div className="guideline-dot"></div>
                  <div className="guideline-text">
                    <h4>Fact Verification</h4>
                    <p>Verify information and cite sources when possible</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default PostNews;
