import React, { useState, useEffect } from 'react';
import { ChevronLeft, Send, Image, Calendar, MapPin, User, FileText, Tag, CheckCircle, AlertCircle } from 'lucide-react';

const PostNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    author: '',
    category: '',
    location: '',
    imageUrl: '',
    tags: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [localNews, setLocalNews] = useState([]);
  const [errors, setErrors] = useState({});

  // Load existing local news on component mount - using in-memory storage
  useEffect(() => {
    // Since localStorage isn't available in artifacts, we'll start with empty array
    setLocalNews([]);
  }, []);

  const categories = [
    'Breaking News',
    'Politics', 
    'Sports',
    'Entertainment',
    'Technology',
    'Health',
    'Business',
    'Education',
    'Weather',
    'Community',
    'Crime',
    'Traffic'
  ];

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    if (!formData.content.trim()) {
      newErrors.content = 'Content is required';
    }
    if (!formData.author.trim()) {
      newErrors.author = 'Author name is required';
    }
    if (!formData.category) {
      newErrors.category = 'Category is required';
    }
    if (formData.imageUrl && !isValidUrl(formData.imageUrl)) {
      newErrors.imageUrl = 'Please enter a valid URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // Create news object
      const newsArticle = {
        id: Date.now().toString(),
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        publishedAt: new Date().toISOString(),
        source: 'Local News',
        isLocal: true
      };

      // Add new article to the beginning of local news array
      const updatedNews = [newsArticle, ...localNews];

      // Update state (since localStorage isn't available in artifacts)
      setLocalNews(updatedNews);

      // Show success message
      setSubmitSuccess(true);

      // Reset form
      setFormData({
        title: '',
        content: '',
        author: '',
        category: '',
        location: '',
        imageUrl: '',
        tags: ''
      });

      // Hide success message after 4 seconds
      setTimeout(() => setSubmitSuccess(false), 4000);

    } catch (error) {
      console.error('Error saving news:', error);
      alert('Error saving news. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Fixed Header */}
      <div className="fixed top-0 left-0 right-0 z-50 bg-gradient-to-r from-indigo-600 via-purple-600 to-purple-700 shadow-xl">
        <div className="max-w-6xl mx-auto px-4 py-4">
          <div className="flex items-center gap-4">
            <button 
              onClick={goBack}
              className="p-3 hover:bg-white hover:bg-opacity-20 rounded-xl transition-all duration-300 text-white hover:scale-105"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <div className="text-white">
              <h1 className="text-2xl md:text-3xl font-bold">Post Local News</h1>
              <p className="text-indigo-100 mt-1">Share breaking news from your community</p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content with proper top padding */}
      <div className="pt-32 pb-8">
        <div className="max-w-6xl mx-auto px-4">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Main Form */}
            <div className="lg:col-span-2">
              <div className="bg-white shadow-2xl rounded-3xl p-6 md:p-8 border border-gray-100">
                <div className="space-y-8">
                  {/* Title */}
                  <div className="group">
                    <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <FileText className="w-4 h-4 text-indigo-600" />
                      News Title *
                    </label>
                    <input
                      type="text"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      placeholder="Enter compelling news headline..."
                      className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02] ${
                        errors.title ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-indigo-500'
                      }`}
                      required
                    />
                    {errors.title && (
                      <p className="text-red-500 text-sm mt-2 flex items-center gap-1 animate-pulse">
                        <AlertCircle className="w-4 h-4" />
                        {errors.title}
                      </p>
                    )}
                  </div>

                  {/* Content */}
                  <div className="group">
                    <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <FileText className="w-4 h-4 text-indigo-600" />
                      News Content *
                    </label>
                    <textarea
                      name="content"
                      value={formData.content}
                      onChange={handleInputChange}
                      placeholder="Write your news article content here... Include who, what, when, where, and why."
                      rows="10"
                      className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-100 transition-all duration-300 resize-none focus:scale-[1.01] ${
                        errors.content ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-indigo-500'
                      }`}
                      required
                    />
                    {errors.content && (
                      <p className="text-red-500 text-sm mt-2 flex items-center gap-1 animate-pulse">
                        <AlertCircle className="w-4 h-4" />
                        {errors.content}
                      </p>
                    )}
                  </div>

                  {/* Author and Category Row */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="group">
                      <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <User className="w-4 h-4 text-indigo-600" />
                        Author Name *
                      </label>
                      <input
                        type="text"
                        name="author"
                        value={formData.author}
                        onChange={handleInputChange}
                        placeholder="Your full name"
                        className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02] ${
                          errors.author ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-indigo-500'
                        }`}
                        required
                      />
                      {errors.author && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1 animate-pulse">
                          <AlertCircle className="w-4 h-4" />
                          {errors.author}
                        </p>
                      )}
                    </div>

                    <div className="group">
                      <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <Tag className="w-4 h-4 text-indigo-600" />
                        Category *
                      </label>
                      <select
                        name="category"
                        value={formData.category}
                        onChange={handleInputChange}
                        className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02] ${
                          errors.category ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-indigo-500'
                        }`}
                        required
                      >
                        <option value="">Select category</option>
                        {categories.map(cat => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                      {errors.category && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1 animate-pulse">
                          <AlertCircle className="w-4 h-4" />
                          {errors.category}
                        </p>
                      )}
                    </div>
                  </div>

                  {/* Location and Image URL Row */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="group">
                      <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <MapPin className="w-4 h-4 text-indigo-600" />
                        Location
                      </label>
                      <input
                        type="text"
                        name="location"
                        value={formData.location}
                        onChange={handleInputChange}
                        placeholder="City, State/Country"
                        className="w-full px-6 py-4 border-2 border-gray-200 rounded-2xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02]"
                      />
                    </div>

                    <div className="group">
                      <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <Image className="w-4 h-4 text-indigo-600" />
                        Image URL
                      </label>
                      <input
                        type="url"
                        name="imageUrl"
                        value={formData.imageUrl}
                        onChange={handleInputChange}
                        placeholder="https://example.com/image.jpg"
                        className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02] ${
                          errors.imageUrl ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-indigo-500'
                        }`}
                      />
                      {errors.imageUrl && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1 animate-pulse">
                          <AlertCircle className="w-4 h-4" />
                          {errors.imageUrl}
                        </p>
                      )}
                    </div>
                  </div>

                  {/* Tags */}
                  <div className="group">
                    <label className="flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <Tag className="w-4 h-4 text-indigo-600" />
                      Tags
                    </label>
                    <input
                      type="text"
                      name="tags"
                      value={formData.tags}
                      onChange={handleInputChange}
                      placeholder="breaking, local, community, urgent"
                      className="w-full px-6 py-4 border-2 border-gray-200 rounded-2xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-100 transition-all duration-300 focus:scale-[1.02]"
                    />
                    <p className="text-sm text-gray-500 mt-2">Separate tags with commas for better categorization</p>
                  </div>

                  {/* Submit Button */}
                  <button
                    type="button"
                    onClick={handleSubmit}
                    disabled={isSubmitting}
                    className="w-full bg-gradient-to-r from-indigo-600 via-purple-600 to-purple-700 hover:from-indigo-700 hover:via-purple-700 hover:to-purple-800 text-white py-5 px-8 rounded-2xl font-bold text-lg disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-3 shadow-2xl hover:shadow-3xl transition-all duration-300 hover:scale-105 active:scale-95"
                  >
                    {isSubmitting ? (
                      <>
                        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                        Publishing Article...
                      </>
                    ) : (
                      <>
                        <Send className="w-6 h-6" />
                        Publish News Article
                      </>
                    )}
                  </button>
                </div>

                {/* Success Message */}
                {submitSuccess && (
                  <div className="mt-8 p-6 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-2xl shadow-2xl animate-bounce">
                    <div className="flex items-center gap-3">
                      <CheckCircle className="w-6 h-6" />
                      <div>
                        <p className="font-bold text-lg">Article Published Successfully! 🎉</p>
                        <p className="text-green-100">Your local news has been added to the community feed.</p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Sidebar */}
            <div className="space-y-6">
              {/* Guidelines */}
              <div className="bg-white shadow-2xl rounded-3xl p-6 border border-gray-100">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                  <FileText className="w-5 h-5 text-indigo-600" />
                  Publishing Guidelines
                </h3>
                <div className="space-y-4 text-sm text-gray-600">
                  <div className="flex items-start gap-3 p-4 bg-gradient-to-r from-purple-50 to-indigo-50 rounded-xl border-l-4 border-purple-500 hover:shadow-lg transition-all duration-300">
                    <div className="w-2 h-2 bg-purple-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong className="text-purple-700">Clear Headlines:</strong> Write compelling, accurate headlines that capture your story's essence</p>
                  </div>
                  <div className="flex items-start gap-3 p-4 bg-gradient-to-r from-blue-50 to-cyan-50 rounded-xl border-l-4 border-blue-500 hover:shadow-lg transition-all duration-300">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong className="text-blue-700">Complete Context:</strong> Include who, what, when, where, and why in your content</p>
                  </div>
                  <div className="flex items-start gap-3 p-4 bg-gradient-to-r from-green-50 to-emerald-50 rounded-xl border-l-4 border-green-500 hover:shadow-lg transition-all duration-300">
                    <div className="w-2 h-2 bg-green-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong className="text-green-700">Proper Categorization:</strong> Choose appropriate categories and add relevant tags</p>
                  </div>
                  <div className="flex items-start gap-3 p-4 bg-gradient-to-r from-orange-50 to-yellow-50 rounded-xl border-l-4 border-orange-500 hover:shadow-lg transition-all duration-300">
                    <div className="w-2 h-2 bg-orange-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong className="text-orange-700">Fact Verification:</strong> Verify information and cite sources when possible</p>
                  </div>
                </div>
              </div>

              {/* Recent Local News */}
              <div className="bg-white shadow-2xl rounded-3xl p-6 border border-gray-100">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                  <Calendar className="w-5 h-5 text-indigo-600" />
                  Recent Local News
                </h3>
                {localNews.length > 0 ? (
                  <div className="space-y-4">
                    {localNews.slice(0, 3).map((news, index) => (
                      <div key={news.id} className="p-4 bg-gradient-to-r from-gray-50 to-gray-100 rounded-xl cursor-pointer hover:shadow-lg transition-all duration-300 border-l-4 border-transparent hover:border-indigo-500 hover:scale-105">
                        <h4 className="font-semibold text-sm text-gray-900 mb-2 line-clamp-2">{news.title}</h4>
                        <div className="flex items-center gap-2 text-xs text-gray-500">
                          <span className="px-3 py-1 bg-gradient-to-r from-purple-100 to-indigo-100 text-purple-700 rounded-full font-medium shadow-md">
                            {news.category}
                          </span>
                          <span>•</span>
                          <span>{new Date(news.publishedAt).toLocaleDateString()}</span>
                        </div>
                        {news.author && (
                          <p className="text-xs text-gray-500 mt-1">By {news.author}</p>
                        )}
                      </div>
                    ))}
                    <div className="text-center pt-4 border-t border-gray-200">
                      <p className="text-xs text-gray-500 bg-gradient-to-r from-indigo-50 to-purple-50 p-3 rounded-lg">
                        <strong className="text-indigo-600">{localNews.length}</strong> total local {localNews.length === 1 ? 'article' : 'articles'} published
                      </p>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <div className="w-16 h-16 bg-gradient-to-r from-indigo-100 to-purple-100 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg">
                      <FileText className="w-8 h-8 text-indigo-600" />
                    </div>
                    <p className="text-gray-500 text-sm">No local news articles yet.</p>
                    <p className="text-indigo-600 font-semibold text-sm mt-1">Be the first to post!</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostNews;
