            }

            .enhanced-nav-link:hover {
              background: rgba(102, 126, 234, 0.1);
              color: #667eea !important;
            }

            .post-news-link {
              color: white !important;
              margin: 0.5rem 0 !important;
            }

            .post-news-link:hover {
              color: white !important;
            }
          }

          /* Pulse animation for active states */
          @keyframes pulse {
            0% { transform: scale(1); }
