import React, { useState, useEffect } from 'react';
import { Clock, User, MapPin, Tag, Image, X, ThumbsUp, ThumbsDown, Eye } from 'lucide-react';

const LocalNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [imageErrors, setImageErrors] = useState({});
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [userInteractions, setUserInteractions] = useState({});
  const [userId] = useState(() => {
    // Generate or retrieve user ID from localStorage (or use authentication)
    let id = localStorage.getItem('userId');
    if (!id) {
      id = 'user_' + Math.random().toString(36).substr(2, 9);
      localStorage.setItem('userId', id);
    }
    return id;
  });
  const articlesPerPage = 12;

  useEffect(() => {
    fetchNews();
    fetchUserInteractions();
  }, []);

  const fetchNews = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3001/api/news');
      if (!response.ok) {
        throw new Error('Failed to fetch news');
      }
      const data = await response.json();
      setNews(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserInteractions = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/user-interactions/${userId}`);
      if (response.ok) {
        const data = await response.json();
        setUserInteractions(data);
      }
    } catch (err) {
      console.error('Failed to fetch user interactions:', err);
    }
  };

  const updateArticleState = (articleId, updates) => {
  // Update list
  setNews(prev =>
    prev.map(article =>
      article._id === articleId ? { ...article, ...updates } : article
    )
  );

  // Update modal if it's open
  if (selectedArticle && selectedArticle._id === articleId) {
    setSelectedArticle(prev => ({ ...prev, ...updates }));
  }
};

const handleLike = async (articleId, e) => {
  e.stopPropagation();
  try {
    const res = await fetch('http://localhost:3001/api/like', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ articleId, userId }),
    });

    if (res.ok) {
      const { likes, dislikes, userLiked, userDisliked } = await res.json();

      updateArticleState(articleId, { likes, dislikes });

      setUserInteractions(prev => ({
        ...prev,
        [articleId]: { liked: userLiked, disliked: userDisliked, viewed: prev[articleId]?.viewed }
      }));
    }
  } catch (err) {
    console.error('Like failed:', err);
  }
};

const handleDislike = async (articleId, e) => {
  e.stopPropagation();
  try {
    const res = await fetch('http://localhost:3001/api/dislike', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ articleId, userId }),
    });

    if (res.ok) {
      const { likes, dislikes, userLiked, userDisliked } = await res.json();

      updateArticleState(articleId, { likes, dislikes });

      setUserInteractions(prev => ({
        ...prev,
        [articleId]: { liked: userLiked, disliked: userDisliked, viewed: prev[articleId]?.viewed }
      }));
    }
  } catch (err) {
    console.error('Dislike failed:', err);
  }
};

const handleView = async (articleId) => {
  try {
    const res = await fetch('http://localhost:3001/api/view', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ articleId, userId }),
    });

    if (res.ok) {
      const { views } = await res.json();

      updateArticleState(articleId, { views });

      setUserInteractions(prev => ({
        ...prev,
        [articleId]: { ...prev[articleId], viewed: true }
      }));
    }
  } catch (err) {
    console.error('View failed:', err);
  }
};


  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatNumber = (num) => {
    if (num >= 1000000) {
      return (num / 1000000).toFixed(1) + 'M';
    } else if (num >= 1000) {
      return (num / 1000).toFixed(1) + 'K';
    }
    return num?.toString() || '0';
  };

  const handleImageError = (articleId) => {
    setImageErrors(prev => ({ ...prev, [articleId]: true }));
  };

  const openFullArticle = async (article) => {
    setSelectedArticle(article);
    document.body.style.overflow = 'hidden';

    // Record view when opening full article
    if (!userInteractions[article._id]?.viewed) {
      await handleView(article._id);
    }
  };

  const closeFullArticle = () => {
    setSelectedArticle(null);
    document.body.style.overflow = 'unset';
  };

  const truncateText = (text, maxLength) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };

  const filteredNews = news.filter(article =>
    article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    article.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
    article.author.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredNews.length / articlesPerPage);
  const startIndex = (currentPage - 1) * articlesPerPage;
  const currentArticles = filteredNews.slice(startIndex, startIndex + articlesPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingContent}>
          <div style={styles.spinner}></div>
          <p style={styles.loadingText}>Loading local news...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.errorContainer}>
        <div style={styles.errorContent}>
          <div style={styles.errorIcon}>⚠️</div>
          <p style={styles.errorText}>Error loading news: {error}</p>
          <button onClick={fetchNews} style={styles.retryButton}>
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.headerTop}>
            <div style={styles.headerLeft}>
              <div style={styles.badge}>
                📰 Local News
              </div>
              <h1 style={styles.title}>Community Stories</h1>
            </div>
            <div style={styles.headerRight}>
              <input
                type="text"
                placeholder="Search news..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={styles.searchInput}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Stats Bar */}
      <div style={styles.statsBar}>
        <div style={styles.statsContent}>
          <div style={styles.statsLeft}>
            <span style={styles.stat}>
              📊 Total Articles: <strong>{news.length}</strong>
            </span>
            <span style={styles.stat}>
              🔍 Showing: <strong>{currentArticles.length}</strong> articles
            </span>
          </div>
          <div style={styles.statsRight}>
            📄 Page: <strong>{currentPage}</strong> of <strong>{totalPages}</strong>
          </div>
        </div>
      </div>

      {/* News Grid */}
      <div style={styles.mainContent}>
        {currentArticles.length === 0 ? (
          <div style={styles.emptyState}>
            <div style={styles.emptyIcon}>📰</div>
            <h3 style={styles.emptyTitle}>No news found</h3>
            <p style={styles.emptyText}>
              {searchTerm ? 'Try adjusting your search terms' : 'No local news articles available yet'}
            </p>
          </div>
        ) : (
          <div style={styles.newsGrid}>
            {currentArticles.map((article) => (
              <div key={article._id} style={styles.newsCard}>
                {/* Article Image */}
                {article.imageUrl && (
                  <div style={styles.imageContainer}>
                    {!imageErrors[article._id] ? (
                      <img
                        src={article.imageUrl}
                        alt={article.title}
                        style={styles.articleImage}
                        onError={() => handleImageError(article._id)}
                      />
                    ) : (
                      <div style={styles.imagePlaceholder}>
                        <Image style={styles.placeholderIcon} />
                        <span style={styles.placeholderText}>Image unavailable</span>
                      </div>
                    )}
                  </div>
                )}

                {/* Article Header */}
                <div style={styles.cardHeader}>
                  <div style={styles.cardHeaderTop}>
                    <span style={styles.sourceTag}>
                      {article.source || 'Local News'}
                    </span>
                    {article.isLocal && (
                      <span style={styles.localTag}>🏠 Local</span>
                    )}
                  </div>

                  <h2 style={styles.cardTitle}>
                    {article.title}
                  </h2>

                  <p style={styles.cardContent}>
                    {truncateText(article.content, 150)}
                  </p>
                </div>

                {/* Engagement Stats */}
                <div style={styles.engagementStats}>
                  <div style={styles.statGroup}>
                    <div style={styles.statItem}>
                      <Eye style={styles.statIcon} />
                      <span style={styles.statValue}>{formatNumber(article.views || 0)}</span>
                    </div>
                    <div style={styles.statItem}>
                      <ThumbsUp style={styles.statIcon} />
                      <span style={styles.statValue}>{formatNumber(article.likes || 0)}</span>
                    </div>
                    <div style={styles.statItem}>
                      <ThumbsDown style={styles.statIcon} />
                      <span style={styles.statValue}>{formatNumber(article.dislikes || 0)}</span>
                    </div>
                  </div>
                </div>

                {/* Article Footer */}
                <div style={styles.cardFooter}>
                  <div style={styles.metaInfo}>
                    <div style={styles.metaItem}>
                      <User style={styles.metaIcon} />
                      <span>{article.author}</span>
                    </div>

                    <div style={styles.metaItem}>
                      <Tag style={styles.metaIcon} />
                      <span style={{textTransform: 'capitalize'}}>{article.category}</span>
                    </div>

                    {article.location && (
                      <div style={styles.metaItem}>
                        <MapPin style={styles.metaIcon} />
                        <span>{article.location}</span>
                      </div>
                    )}

                    <div style={styles.metaItem}>
                      <Clock style={styles.metaIcon} />
                      <span>{formatDate(article.publishedAt || article.createdAt)}</span>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div style={styles.actionButtons}>
                    <div style={styles.interactionButtons}>
                      <button
                        style={{
                          ...styles.interactionButton,
                          ...(userInteractions[article._id]?.liked ? styles.interactionButtonActive : {}),
                          color: userInteractions[article._id]?.liked ? '#16a34a' : '#64748b'
                        }}
                        onClick={(e) => handleLike(article._id, e)}
                        title={userInteractions[article._id]?.liked ? 'Remove like' : 'Like this article'}
                      >
                        <ThumbsUp style={styles.interactionIcon} />
                        <span>{formatNumber(article.likes || 0)}</span>
                      </button>

                      <button
                        style={{
                          ...styles.interactionButton,
                          ...(userInteractions[article._id]?.disliked ? styles.interactionButtonActive : {}),
                          color: userInteractions[article._id]?.disliked ? '#dc2626' : '#64748b'
                        }}
                        onClick={(e) => handleDislike(article._id, e)}
                        title={userInteractions[article._id]?.disliked ? 'Remove dislike' : 'Dislike this article'}
                      >
                        <ThumbsDown style={styles.interactionIcon} />
                        <span>{formatNumber(article.dislikes || 0)}</span>
                      </button>
                    </div>

                    <button 
                      style={styles.readButton}
                      onClick={() => openFullArticle(article)}
                    >
                      📖 Read Full Article
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div style={styles.pagination}>
            <div style={styles.paginationContent}>
              <button
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
                style={{
                  ...styles.paginationButton,
                  ...(currentPage === 1 ? styles.paginationButtonDisabled : {})
                }}
              >
                Previous
              </button>

              {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                <button
                  key={page}
                  onClick={() => handlePageChange(page)}
                  style={{
                    ...styles.paginationButton,
                    ...(page === currentPage ? styles.paginationButtonActive : {})
                  }}
                >
                  {page}
                </button>
              ))}

              <button
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
                style={{
                  ...styles.paginationButton,
                  ...(currentPage === totalPages ? styles.paginationButtonDisabled : {})
                }}
              >
                Next
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Full Article Modal */}
      {selectedArticle && (
        <div style={styles.modalOverlay} onClick={closeFullArticle}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            {/* Modal Header */}
            <div style={styles.modalHeader}>
              <div style={styles.modalHeaderContent}>
                <div style={styles.modalHeaderTop}>
                  <span style={styles.sourceTag}>
                    {selectedArticle.source || 'Local News'}
                  </span>
                  {selectedArticle.isLocal && (
                    <span style={styles.localTag}>🏠 Local</span>
                  )}
                </div>
                <button style={styles.closeButton} onClick={closeFullArticle}>
                  <X style={styles.closeIcon} />
                </button>
              </div>

              <h1 style={styles.modalTitle}>{selectedArticle.title}</h1>

              <div style={styles.modalMeta}>
                <div style={styles.metaItem}>
                  <User style={styles.metaIcon} />
                  <span>{selectedArticle.author}</span>
                </div>

                <div style={styles.metaItem}>
                  <Tag style={styles.metaIcon} />
                  <span style={{textTransform: 'capitalize'}}>{selectedArticle.category}</span>
                </div>

                {selectedArticle.location && (
                  <div style={styles.metaItem}>
                    <MapPin style={styles.metaIcon} />
                    <span>{selectedArticle.location}</span>
                  </div>
                )}

                <div style={styles.metaItem}>
                  <Clock style={styles.metaIcon} />
                  <span>{formatDate(selectedArticle.publishedAt || selectedArticle.createdAt)}</span>
                </div>
              </div>

              {/* Modal Engagement Stats */}
              <div style={styles.modalEngagementStats}>
                <div style={styles.modalStatGroup}>
                  <div style={styles.modalStatItem}>
                    <Eye style={styles.modalStatIcon} />
                    <span style={styles.modalStatValue}>{formatNumber(selectedArticle.views || 0)} views</span>
                  </div>
                  <div style={styles.modalStatItem}>
                    <ThumbsUp style={styles.modalStatIcon} />
                    <span style={styles.modalStatValue}>{formatNumber(selectedArticle.likes || 0)} likes</span>
                  </div>
                  <div style={styles.modalStatItem}>
                    <ThumbsDown style={styles.modalStatIcon} />
                    <span style={styles.modalStatValue}>{formatNumber(selectedArticle.dislikes || 0)} dislikes</span>
                  </div>
                </div>
              </div>

              {/* Modal Action Buttons */}
              <div style={styles.modalActionButtons}>
                <button
                  style={{
                    ...styles.modalInteractionButton,
                    ...(userInteractions[selectedArticle._id]?.liked ? styles.modalInteractionButtonLiked : {})
                  }}
                  onClick={(e) => handleLike(selectedArticle._id, e)}
                >
                  <ThumbsUp style={styles.modalInteractionIcon} />
                  <span>{userInteractions[selectedArticle._id]?.liked ? 'Liked' : 'Like'}</span>
                </button>

                <button
                  style={{
                    ...styles.modalInteractionButton,
                    ...(userInteractions[selectedArticle._id]?.disliked ? styles.modalInteractionButtonDisliked : {})
                  }}
                  onClick={(e) => handleDislike(selectedArticle._id, e)}
                >
                  <ThumbsDown style={styles.modalInteractionIcon} />
                  <span>{userInteractions[selectedArticle._id]?.disliked ? 'Disliked' : 'Dislike'}</span>
                </button>
              </div>
            </div>

            {/* Modal Image */}
            {selectedArticle.imageUrl && (
              <div style={styles.modalImageContainer}>
                {!imageErrors[selectedArticle._id] ? (
                  <img
                    src={selectedArticle.imageUrl}
                    alt={selectedArticle.title}
                    style={styles.modalImage}
                    onError={() => handleImageError(selectedArticle._id)}
                  />
                ) : (
                  <div style={styles.modalImagePlaceholder}>
                    <Image style={styles.placeholderIcon} />
                    <span style={styles.placeholderText}>Image unavailable</span>
                  </div>
                )}
              </div>
            )}

            {/* Modal Body */}
            <div style={styles.modalBody}>
              <div style={styles.modalText}>
                {selectedArticle.content.split('\n').map((paragraph, index) => (
                  <p key={index} style={styles.modalParagraph}>
                    {paragraph}
                  </p>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

