import React, { Component } from 'react'
import NewsItem from './NewItem1'
import Spinner from './Spinner'

export class News extends Component {
    articles = []
    page_no = 1;
    totalResults = 0
    page_size = 12

    constructor(){
        super();
        this.state = {
            articles : this.articles,
            page_no : this.page_no,
            totalResults : this.totalResults,
            page_size : this.page_size,
            loading : false,
            viewMode: 'grid',
            sortBy: 'publishedAt',
            searchQuery: '',
            // AI Features
            aiSummaries: {}, // Store AI summaries by article URL
            loadingSummaries: {}, // Track which summaries are loading
            aiAnalysis: {}, // Store AI analysis
            sentimentAnalysis: {}, // Store sentiment analysis
            aiRecommendations: [], // AI-based article recommendations
            loadingRecommendations: false
        }
    }

    async componentDidMount(){
        await this.fetchNews();
        await this.fetchAIRecommendations();
    }

    // Enhanced fetch method
    fetchNews = async (page = 1) => {
        let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.category}&apiKey=ea68187f71374f6d8c64a5794237c8c1&page=${page}&pageSize=${this.state.page_size}&sortBy=${this.state.sortBy}`
        this.setState({loading : true})

        try {
            let data = await fetch(url);
            let parsed_data = await data.json();

            this.setState({
                articles : parsed_data.articles,
                totalResults : parsed_data.totalResults,
                page_no: page,
                loading : false
            })

            // Auto-generate AI insights for first few articles
            this.generateBulkAIInsights(parsed_data.articles.slice(0, 3));

        } catch (error) {
            console.error('Error fetching news:', error);
            this.setState({loading: false});
        }
    }

    // AI Summary Generation
    generateAISummary = async (articleUrl, articleText) => {
        this.setState(prevState => ({
            loadingSummaries: {
                ...prevState.loadingSummaries,
                [articleUrl]: true
            }
        }));

        try {
            const response = await fetch('https://api.openai.com/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}` // You need to add this
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: [
                        {
                            role: "system",
                            content: "You are a professional news summarizer. Create concise, informative summaries that capture the key points of news articles."
                        },
                        {
                            role: "user",
                            content: `Please provide a concise summary of this news article in 2-3 sentences: ${articleText}`
                        }
                    ],
                    max_tokens: 150,
                    temperature: 0.7
                })
            });

            if (!response.ok) throw new Error('Failed to generate summary');

            const data = await response.json();
            const summary = data.choices[0].message.content;

            this.setState(prevState => ({
                aiSummaries: {
                    ...prevState.aiSummaries,
                    [articleUrl]: summary
                },
                loadingSummaries: {
                    ...prevState.loadingSummaries,
                    [articleUrl]: false
                }
            }));

        } catch (error) {
            console.error('Error generating AI summary:', error);
            this.setState(prevState => ({
                aiSummaries: {
                    ...prevState.aiSummaries,
                    [articleUrl]: 'Unable to generate summary at this time.'
                },
                loadingSummaries: {
                    ...prevState.loadingSummaries,
                    [articleUrl]: false
                }
            }));
        }
    }

    // AI Sentiment Analysis
    generateSentimentAnalysis = async (articleUrl, articleText) => {
        try {
            const response = await fetch('https://api.openai.com/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: [
                        {
                            role: "system",
                            content: "Analyze the sentiment of news articles. Respond with: POSITIVE, NEGATIVE, or NEUTRAL, followed by a brief explanation."
                        },
                        {
                            role: "user",
                            content: `Analyze the sentiment of this news article: ${articleText}`
                        }
                    ],
                    max_tokens: 100
                })
            });

            const data = await response.json();
            const sentiment = data.choices[0].message.content;

            this.setState(prevState => ({
                sentimentAnalysis: {
                    ...prevState.sentimentAnalysis,
                    [articleUrl]: sentiment
                }
            }));

        } catch (error) {
            console.error('Error analyzing sentiment:', error);
        }
    }

    // AI-Powered Article Analysis
    generateAIAnalysis = async (articleUrl, articleText) => {
        try {
            const response = await fetch('https://api.openai.com/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: [
                        {
                            role: "system",
                            content: "You are a news analyst. Provide key insights including: main topics, important entities, potential impact, and credibility assessment."
                        },
                        {
                            role: "user",
                            content: `Analyze this news article and provide insights: ${articleText}`
                        }
                    ],
                    max_tokens: 200
                })
            });

            const data = await response.json();
            const analysis = data.choices[0].message.content;

            this.setState(prevState => ({
                aiAnalysis: {
                    ...prevState.aiAnalysis,
                    [articleUrl]: analysis
                }
            }));

        } catch (error) {
            console.error('Error generating analysis:', error);
        }
    }

    // Bulk AI Insights Generation
    generateBulkAIInsights = async (articles) => {
        for (const article of articles) {
            if (article.description || article.content) {
                const articleText = `${article.title}. ${article.description || ''}`;

                // Generate all AI insights
                this.generateSentimentAnalysis(article.url, articleText);
                setTimeout(() => {
                    this.generateAIAnalysis(article.url, articleText);
                }, 1000); // Stagger requests to avoid rate limits
            }
        }
    }

    // AI-Powered Recommendations
    fetchAIRecommendations = async () => {
        this.setState({ loadingRecommendations: true });

        try {
            // Get user's reading history from localStorage or create preferences
            const userPreferences = this.getUserPreferences();

            const response = await fetch('https://api.openai.com/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: [
                        {
                            role: "system",
                            content: "You are a news recommendation engine. Based on user preferences, suggest relevant news topics and keywords."
                        },
                        {
                            role: "user",
                            content: `Based on these preferences: ${userPreferences}, suggest 5 relevant news topics or keywords for recommendations.`
                        }
                    ],
                    max_tokens: 150
                })
            });

            const data = await response.json();
            const recommendations = data.choices[0].message.content.split('\n').filter(line => line.trim());

            this.setState({
                aiRecommendations: recommendations,
                loadingRecommendations: false
            });

        } catch (error) {
            console.error('Error fetching AI recommendations:', error);
            this.setState({ loadingRecommendations: false });
        }
    }

    // Get user preferences (you can enhance this)
    getUserPreferences = () => {
        const category = this.props.category || 'general';
        return `Interested in ${category} news, prefers recent articles`;
    }

    // Smart Search with AI
    performAISearch = async (query) => {
        try {
            const response = await fetch('https://api.openai.com/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: [
                        {
                            role: "system",
                            content: "Convert user search queries into optimized news search keywords. Return only the keywords."
                        },
                        {
                            role: "user",
                            content: `Optimize this search query for news: "${query}"`
                        }
                    ],
                    max_tokens: 50
                })
            });

            const data = await response.json();
            const optimizedQuery = data.choices[0].message.content;

            // Use optimized query for search
            this.setState({ searchQuery: optimizedQuery });

        } catch (error) {
            console.error('Error optimizing search:', error);
            this.setState({ searchQuery: query });
        }
    }

    // Existing methods
    handlenext = async () => {
        if(this.state.page_no + 1 <= Math.ceil(this.state.totalResults/this.state.page_size)){
            await this.fetchNews(this.state.page_no + 1);
        }
    }

    handleprev = async () => {
        if(this.state.page_no > 1) {
            await this.fetchNews(this.state.page_no - 1);
        }
    }

    toggleViewMode = () => {
        this.setState({
            viewMode: this.state.viewMode === 'grid' ? 'list' : 'grid'
        });
    }

    handleSort = (sortType) => {
        this.setState({sortBy: sortType}, () => {
            this.fetchNews(1);
        });
    }

    handleSearch = (query) => {
        // Use AI to optimize search if query is complex
        if (query.length > 10) {
            this.performAISearch(query);
        } else {
            this.setState({searchQuery: query});
        }
    }

    getFilteredArticles = () => {
        if (!this.state.searchQuery) return this.state.articles;

        return this.state.articles.filter(article => 
            article.title.toLowerCase().includes(this.state.searchQuery.toLowerCase()) ||
            (article.description && article.description.toLowerCase().includes(this.state.searchQuery.toLowerCase()))
        );
    }

    getCategoryDisplayName = () => {
        const category = this.props.category || 'general';
        return category.charAt(0).toUpperCase() + category.slice(1);
    }

    render() {
        const filteredArticles = this.getFilteredArticles();
        const totalPages = Math.ceil(this.state.totalResults / this.state.page_size);

        return (
            <div className="news-container">
                <div className="container">
                    {/* Header */}
                    <div className="news-header">
                        <h1 className="news-title">
                            ü§ñ AI-Powered {this.getCategoryDisplayName()} News
                        </h1>
                        <p className="text-center text-muted mb-0">
                            Enhanced with AI summaries, sentiment analysis, and smart recommendations
                        </p>
                    </div>

                    {/* AI Recommendations Panel */}
                    {this.state.aiRecommendations.length > 0 && (
                        <div className="ai-recommendations">
                            <h4>üéØ AI Recommendations</h4>
                            <div className="recommendations-list">
                                {this.state.aiRecommendations.slice(0, 3).map((rec, index) => (
                                    <span key={index} className="recommendation-tag">
                                        {rec}
                                    </span>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* Controls */}
                    <div className="news-controls">
                        <div className="control-group">
                            <button 
                                className={`enhanced-btn ${this.state.viewMode === 'grid' ? '' : 'outline'}`}
                                onClick={this.toggleViewMode}
                            >
                                {this.state.viewMode === 'grid' ? 'üìä Grid View' : 'üìã List View'}
                            </button>

                            <select 
                                className="form-select"
                                value={this.state.sortBy}
                                onChange={(e) => this.handleSort(e.target.value)}
                            >
                                <option value="publishedAt">üìÖ Latest First</option>
                                <option value="popularity">üî• Most Popular</option>
                                <option value="relevancy">üéØ Most Relevant</option>
                            </select>
                        </div>

                        <div className="search-box">
                            <span className="search-icon">üîç</span>
                            <input
                                type="text"
                                className="search-input"
                                placeholder="AI-enhanced search..."
                                value={this.state.searchQuery}
                                onChange={(e) => this.handleSearch(e.target.value)}
                            />
                        </div>
                    </div>

                    {/* Stats Bar */}
                    <div className="stats-bar">
                        <div>üìä Total: {this.state.totalResults}</div>
                        <div>üìÑ Showing: {filteredArticles.length}</div>
                        <div>ü§ñ AI Enhanced: {Object.keys(this.state.aiSummaries).length}</div>
                        <div>üìñ Page: {this.state.page_no} of {totalPages}</div>
                    </div>

                    {/* Loading */}
                    {this.state.loading && (
                        <div className="text-center py-5">
                            <Spinner/>
                        </div>
                    )}

                    {/* News Grid/List */}
                    {!this.state.loading && (
                        <>
                            {filteredArticles.length > 0 ? (
                                <div className={this.state.viewMode === 'grid' ? 'grid-container' : 'list-container'}>
                                    {filteredArticles.map((element, index) => {
                                        return element.urlToImage && (
                                            <div key={element.url || index}>
                                                <NewsItem 
                                                    title={element.title}  
                                                    desc={element.description} 
                                                    urlToImage={element.urlToImage} 
                                                    url={element.url}
                                                    author={element.author}
                                                    publishedAt={element.publishedAt}
                                                    source={element.source.name}
                                                    viewMode={this.state.viewMode}
                                                    // AI Features Props
                                                    aiSummary={this.state.aiSummaries[element.url]}
                                                    loadingSummary={this.state.loadingSummaries[element.url]}
                                                    aiAnalysis={this.state.aiAnalysis[element.url]}
                                                    sentimentAnalysis={this.state.sentimentAnalysis[element.url]}
                                                    onGenerateSummary={() => this.generateAISummary(element.url, `${element.title}. ${element.description || ''}`)}
                                                    onGenerateAnalysis={() => this.generateAIAnalysis(element.url, `${element.title}. ${element.description || ''}`)}
                                                />
                                            </div>
                                        )
                                    })}
                                </div>
                            ) : (
                                <div className="empty-state">
                                    <h3>üîç No articles found</h3>
                                    <p>Try adjusting your search terms or check back later.</p>
                                </div>
                            )}
                        </>
                    )}

                    {/* Pagination */}
                    {!this.state.loading && this.state.totalResults > 0 && (
                        <div className="pagination-enhanced">
                            <button 
                                type="button" 
                                disabled={this.state.page_no <= 1} 
                                onClick={this.handleprev} 
                                className="enhanced-btn"
                            >
                                ‚¨ÖÔ∏è Previous
                            </button>

                            <div className="page-info">
                                Page {this.state.page_no} of {totalPages}
                            </div>

                            <button 
                                type="button" 
                                disabled={this.state.page_no >= totalPages} 
                                onClick={this.handlenext} 
                                className="enhanced-btn"
                            >
                                Next ‚û°Ô∏è
                            </button>
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

export default News
