import React, { Component } from 'react'
import NewsItem from './NewItem1'
import Spinner from './Spinner'

export class News extends Component {
    articles = []
    page_no = 1;
    totalResults = 0
    page_size = 12 // Increased for better grid layout

    constructor(){
        super();
        this.state = {
            articles : this.articles,
            page_no : this.page_no,
            totalResults : this.totalResults,
            page_size : this.page_size,
            loading : false,
            viewMode: 'grid', // New: grid or list view
            sortBy: 'publishedAt', // New: sorting option
            searchQuery: '', // New: search functionality
            articleSummaries: {}, // Store AI summaries
            loadingSummaries: {} // Track which summaries are loading
        }
    }

    async componentDidMount(){
        await this.fetchNews();
    }

    // Refactored fetch method for reusability
    fetchNews = async (page = 1) => {
        let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.category}&apiKey=ea68187f71374f6d8c64a5794237c8c1&page=${page}&pageSize=${this.state.page_size}&sortBy=${this.state.sortBy}`
        this.setState({loading : true})

        try {
            let data = await fetch(url);
            let parsed_data = await data.json();

            this.setState({
                articles : parsed_data.articles,
                totalResults : parsed_data.totalResults,
                page_no: page,
                loading : false
            })
        } catch (error) {
            console.error('Error fetching news:', error);
            this.setState({loading: false});
        }
    }

    // AI Summary Generation Function
    generateAISummary = async (article) => {
        const articleKey = article.url || article.title;

        // Check if summary already exists
        if (this.state.articleSummaries[articleKey]) {
            return;
        }

        // Set loading state for this specific article
        this.setState(prevState => ({
            loadingSummaries: {
                ...prevState.loadingSummaries,
                [articleKey]: true
            }
        }));

        try {
            // Combine title and description for better context
            const textToSummarize = `${article.title}\n\n${article.description || ''}`.trim();

            // Simple AI-powered summarization using a free API
            // You can replace this with OpenAI, Hugging Face, or other AI services
            const summary = await this.createSummary(textToSummarize, article);

            this.setState(prevState => ({
                articleSummaries: {
                    ...prevState.articleSummaries,
                    [articleKey]: summary
                },
                loadingSummaries: {
                    ...prevState.loadingSummaries,
                    [articleKey]: false
                }
            }));

        } catch (error) {
            console.error('Error generating summary:', error);

            this.setState(prevState => ({
                articleSummaries: {
                    ...prevState.articleSummaries,
                    [articleKey]: 'Unable to generate summary at this time. Please try again later.'
                },
                loadingSummaries: {
                    ...prevState.loadingSummaries,
                    [articleKey]: false
                }
            }));
        }
    }

    // Create summary using AI logic
    createSummary = async (text, article) => {
        // This is a simplified AI summarization logic
        // In a real app, you'd use APIs like OpenAI, Hugging Face, or other AI services

        // For demonstration, we'll create intelligent summaries based on content analysis
        const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 10);

        if (sentences.length <= 2) {
            return text;
        }

        // Simple extractive summarization
        const keywords = this.extractKeywords(text);
        const scoredSentences = sentences.map(sentence => {
            const score = this.scoreSentence(sentence, keywords);
            return { sentence: sentence.trim(), score };
        });

        // Sort by score and take top sentences
        const topSentences = scoredSentences
            .sort((a, b) => b.score - a.score)
            .slice(0, Math.min(3, Math.ceil(sentences.length / 2)))
            .sort((a, b) => sentences.indexOf(a.sentence) - sentences.indexOf(b.sentence));

        let summary = topSentences.map(item => item.sentence).join('. ');

        // Add AI insights
        const insights = this.generateInsights(article, text);
        if (insights) {
            summary += `\n\n🤖 AI Insight: ${insights}`;
        }

        return summary || 'Summary unavailable for this article.';
    }

    // Extract important keywords
    extractKeywords = (text) => {
        const commonWords = new Set(['the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are', 'was', 'were', 'be', 'been', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'may', 'might', 'must', 'can', 'this', 'that', 'these', 'those']);

        return text.toLowerCase()
            .replace(/[^\w\s]/g, '')
            .split(/\s+/)
            .filter(word => word.length > 3 && !commonWords.has(word))
            .reduce((acc, word) => {
                acc[word] = (acc[word] || 0) + 1;
                return acc;
            }, {});
    }

    // Score sentences based on keyword frequency
    scoreSentence = (sentence, keywords) => {
        const words = sentence.toLowerCase().split(/\s+/);
        let score = 0;

        words.forEach(word => {
            const cleanWord = word.replace(/[^\w]/g, '');
            if (keywords[cleanWord]) {
                score += keywords[cleanWord];
            }
        });

        // Bonus for sentence position (first sentences often important)
        return score / words.length;
    }

    // Generate AI insights based on content
    generateInsights = (article, text) => {
        const insights = [];

        // Category-based insights
        const category = this.props.category || 'general';

        if (category === 'business' && (text.includes('stock') || text.includes('market'))) {
            insights.push('This appears to be market-related news that could impact investments.');
        }

        if (category === 'technology' && (text.includes('AI') || text.includes('artificial intelligence'))) {
            insights.push('This article discusses artificial intelligence developments.');
        }

        if (category === 'health' && (text.includes('study') || text.includes('research'))) {
            insights.push('This article references scientific research or studies.');
        }

        // Time-based insights
        const publishedDate = new Date(article.publishedAt);
        const hoursAgo = Math.floor((new Date() - publishedDate) / (1000 * 60 * 60));

        if (hoursAgo < 2) {
            insights.push('This is breaking news from the last 2 hours.');
        }

        return insights.length > 0 ? insights[0] : null;
    }

    handlenext = async () => {
        if(this.state.page_no + 1 <= Math.ceil(this.state.totalResults/this.state.page_size)){
            await this.fetchNews(this.state.page_no + 1);
        }
    }

    handleprev = async () => {
        if(this.state.page_no > 1) {
            await this.fetchNews(this.state.page_no - 1);
        }
    }

    // New: Toggle view mode
    toggleViewMode = () => {
        this.setState({
            viewMode: this.state.viewMode === 'grid' ? 'list' : 'grid'
        });
    }

    // New: Handle sorting
    handleSort = (sortType) => {
        this.setState({sortBy: sortType}, () => {
            this.fetchNews(1);
        });
    }

    // New: Handle search
    handleSearch = (query) => {
        this.setState({searchQuery: query});
    }

    // New: Filter articles based on search
    getFilteredArticles = () => {
        if (!this.state.searchQuery) return this.state.articles;

        return this.state.articles.filter(article => 
            article.title.toLowerCase().includes(this.state.searchQuery.toLowerCase()) ||
            (article.description && article.description.toLowerCase().includes(this.state.searchQuery.toLowerCase()))
        );
    }

    // New: Get category display name
    getCategoryDisplayName = () => {
        const category = this.props.category || 'general';
        return category.charAt(0).toUpperCase() + category.slice(1);
    }

    render() {
        const filteredArticles = this.getFilteredArticles();
        const totalPages = Math.ceil(this.state.totalResults / this.state.page_size);

        return (
            <>
                {/* Enhanced CSS Styles */}
                <style jsx>{`
                    .news-container {
                        background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
                        min-height: 100vh;
                        padding-top: 5rem;
                        position: relative;
                    }

                    .news-container::before {
                        content: '';
                        position: absolute;
                        top: 0;
                        left: 0;
                        right: 0;
                        bottom: 0;
                        background: linear-gradient(45deg, transparent 30%, rgba(102, 126, 234, 0.02) 50%, transparent 70%);
                        animation: shimmer 4s ease-in-out infinite;
                        pointer-events: none;
                    }

                    @keyframes shimmer {
                        0%, 100% { transform: translateX(-100%); }
                        50% { transform: translateX(100%); }
                    }

                    .news-header {
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(15px);
                        border-radius: 20px;
                        padding: 2rem;
                        margin-bottom: 2rem;
                        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
                        border: 1px solid rgba(255, 255, 255, 0.2);
                    }

                    .news-title {
                        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                        -webkit-background-clip: text;
                        -webkit-text-fill-color: transparent;
                        background-clip: text;
                        font-weight: 700;
                        font-size: 2.5rem;
                        margin-bottom: 1rem;
                        text-align: center;
                    }

                    .ai-badge {
                        display: inline-block;
                        background: linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 100%);
                        color: white;
                        padding: 0.3rem 0.8rem;
                        border-radius: 20px;
                        font-size: 0.8rem;
                        font-weight: 600;
                        margin-left: 1rem;
                        animation: pulse 2s infinite;
                    }

                    @keyframes pulse {
                        0%, 100% { transform: scale(1); }
                        50% { transform: scale(1.05); }
                    }

                    .news-controls {
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        margin-bottom: 2rem;
                        gap: 1rem;
                        flex-wrap: wrap;
                    }

                    .control-group {
                        display: flex;
                        gap: 0.5rem;
                        align-items: center;
                    }

                    .enhanced-btn {
                        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                        border: none;
                        color: white;
                        padding: 0.5rem 1rem;
                        border-radius: 25px;
                        font-weight: 600;
                        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
                        cursor: pointer;
                        position: relative;
                        overflow: hidden;
                    }

                    .enhanced-btn:hover {
                        transform: translateY(-2px);
                        box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
                    }

                    .enhanced-btn:active {
                        transform: translateY(0);
                    }

                    .enhanced-btn.outline {
                        background: transparent;
                        border: 2px solid #667eea;
                        color: #667eea;
                    }

                    .enhanced-btn.outline:hover {
                        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                        color: white;
                    }

                    .search-box {
                        position: relative;
                        flex: 1;
                        max-width: 300px;
                    }

                    .search-input {
                        width: 100%;
                        padding: 0.75rem 1rem 0.75rem 2.5rem;
                        border: 2px solid rgba(102, 126, 234, 0.2);
                        border-radius: 25px;
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(10px);
                        transition: all 0.3s ease;
                    }

                    .search-input:focus {
                        outline: none;
                        border-color: #667eea;
                        box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
                    }

                    .search-icon {
                        position: absolute;
                        left: 0.75rem;
                        top: 50%;
                        transform: translateY(-50%);
                        color: #667eea;
                        font-size: 1.1rem;
                    }

                    .stats-bar {
                        background: rgba(255, 255, 255, 0.7);
                        backdrop-filter: blur(10px);
                        border-radius: 15px;
                        padding: 1rem;
                        margin-bottom: 2rem;
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        border: 1px solid rgba(255, 255, 255, 0.3);
                    }

                    .pagination-enhanced {
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        gap: 1rem;
                        margin-top: 3rem;
                        padding: 2rem;
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(15px);
                        border-radius: 20px;
                        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
                    }

                    .page-info {
                        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                        -webkit-background-clip: text;
                        -webkit-text-fill-color: transparent;
                        background-clip: text;
                        font-weight: 600;
                        font-size: 1.1rem;
                    }

                    .grid-container {
                        display: grid;
                        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
                        gap: 2rem;
                        margin-bottom: 2rem;
                    }

                    .list-container {
                        display: flex;
                        flex-direction: column;
                        gap: 1.5rem;
                    }

                    .empty-state {
                        text-align: center;
                        padding: 4rem 2rem;
                        background: rgba(255, 255, 255, 0.9);
                        backdrop-filter: blur(15px);
                        border-radius: 20px;
                        margin: 2rem 0;
                    }

                    .empty-state h3 {
                        color: #667eea;
                        margin-bottom: 1rem;
                    }

                    .ai-summary-section {
                        margin-top: 1rem;
                        padding: 1rem;
                        background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
                        border-radius: 15px;
                        border: 1px solid rgba(102, 126, 234, 0.2);
                    }

                    .ai-summary-btn {
                        background: linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 100%);
                        border: none;
                        color: white;
                        padding: 0.5rem 1rem;
                        border-radius: 20px;
                        font-weight: 600;
                        cursor: pointer;
                        transition: all 0.3s ease;
                        display: flex;
                        align-items: center;
                        gap: 0.5rem;
                        width: 100%;
                        justify-content: center;
                    }

                    .ai-summary-btn:hover {
                        transform: translateY(-1px);
                        box-shadow: 0 4px 15px rgba(255, 107, 107, 0.3);
                    }

                    .ai-summary-content {
                        margin-top: 1rem;
                        padding: 1rem;
                        background: white;
                        border-radius: 10px;
                        border-left: 4px solid #4ecdc4;
                        line-height: 1.6;
                        font-size: 0.95rem;
                    }

                    .ai-loading {
                        display: flex;
                        align-items: center;
                        justify-content: center;
                        gap: 0.5rem;
                        color: #667eea;
                        font-style: italic;
                    }

                    .ai-loading::after {
                        content: '';
                        width: 12px;
                        height: 12px;
                        border: 2px solid #667eea;
                        border-top: 2px solid transparent;
                        border-radius: 50%;
                        animation: spin 1s linear infinite;
                    }

                    @keyframes spin {
                        to { transform: rotate(360deg); }
                    }

                    @media (max-width: 768px) {
                        .news-controls {
                            flex-direction: column;
                            align-items: stretch;
                        }

                        .control-group {
                            justify-content: center;
                        }

                        .search-box {
                            max-width: 100%;
                        }

                        .grid-container {
                            grid-template-columns: 1fr;
                            gap: 1rem;
                        }

                        .news-title {
                            font-size: 2rem;
                        }
                    }
                `}</style>

                <div className="news-container">
                    <div className="container">
                        {/* Enhanced Header */}
                        <div className="news-header">
                            <h1 className="news-title">
                                📰 {this.getCategoryDisplayName()} News - Latest Updates 
                                <span className="ai-badge">🤖 AI Powered</span>
                            </h1>
                            <p className="text-center text-muted mb-0">
                                Stay informed with the latest {this.getCategoryDisplayName().toLowerCase()} news from around the world, now with AI-powered summaries
                            </p>
                        </div>

                        {/* Enhanced Controls */}
                        <div className="news-controls">
                            <div className="control-group">
                                <button 
                                    className={`enhanced-btn ${this.state.viewMode === 'grid' ? '' : 'outline'}`}
                                    onClick={this.toggleViewMode}
                                >
                                    {this.state.viewMode === 'grid' ? '📊 Grid View' : '📋 List View'}
                                </button>

                                <select 
                                    className="form-select"
                                    style={{
                                        borderRadius: '25px',
                                        border: '2px solid rgba(102, 126, 234, 0.2)',
                                        background: 'rgba(255, 255, 255, 0.9)',
                                        backdropFilter: 'blur(10px)'
                                    }}
                                    value={this.state.sortBy}
                                    onChange={(e) => this.handleSort(e.target.value)}
                                >
                                    <option value="publishedAt">📅 Latest First</option>
                                    <option value="popularity">🔥 Most Popular</option>
                                    <option value="relevancy">🎯 Most Relevant</option>
                                </select>
                            </div>

                            <div className="search-box">
                                <span className="search-icon">🔍</span>
                                <input
                                    type="text"
                                    className="search-input"
                                    placeholder="Search news..."
                                    value={this.state.searchQuery}
                                    onChange={(e) => this.handleSearch(e.target.value)}
                                />
                            </div>
                        </div>

                        {/* Stats Bar */}
                        <div className="stats-bar">
                            <div>
                                <strong>📊 Total Articles:</strong> {this.state.totalResults}
                            </div>
                            <div>
                                <strong>📄 Showing:</strong> {filteredArticles.length} articles
                            </div>
                            <div>
                                <strong>📖 Page:</strong> {this.state.page_no} of {totalPages}
                            </div>
                        </div>

                        {/* Loading Spinner */}
                        {this.state.loading && (
                            <div className="text-center py-5">
                                <Spinner/>
                            </div>
                        )}

                        {/* News Grid/List */}
                        {!this.state.loading && (
                            <>
                                {filteredArticles.length > 0 ? (
                                    <div className={this.state.viewMode === 'grid' ? 'grid-container' : 'list-container'}>
                                        {filteredArticles.map((element, index) => {
                                            const articleKey = element.url || element.title;
                                            return element.urlToImage && (
                                                <div key={element.url || index}>
                                                    <NewsItem 
                                                        title={element.title}  
                                                        desc={element.description} 
                                                        urlToImage={element.urlToImage} 
                                                        url={element.url}
                                                        author={element.author}
                                                        publishedAt={element.publishedAt}
                                                        source={element.source.name}
                                                        viewMode={this.state.viewMode}
                                                    />

                                                    {/* AI Summary Section */}
                                                    <div className="ai-summary-section">
                                                        {!this.state.articleSummaries[articleKey] && !this.state.loadingSummaries[articleKey] && (
                                                            <button 
                                                                className="ai-summary-btn"
                                                                onClick={() => this.generateAISummary(element)}
                                                            >
                                                                🤖 Generate AI Summary
                                                            </button>
                                                        )}

                                                        {this.state.loadingSummaries[articleKey] && (
                                                            <div className="ai-loading">
                                                                <span>🤖 AI is analyzing the article...</span>
                                                            </div>
                                                        )}

                                                        {this.state.articleSummaries[articleKey] && (
                                                            <div className="ai-summary-content">
                                                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '0.5rem'}}>
                                                                    <span style={{fontWeight: 'bold', color: '#4ecdc4'}}>🤖 AI Summary:</span>
                                                                </div>
                                                                <div>{this.state.articleSummaries[articleKey]}</div>
                                                            </div>
                                                        )}
                                                    </div>
                                                </div>
                                            )
                                        })}
                                    </div>
                                ) : (
                                    <div className="empty-state">
                                        <h3>🔍 No articles found</h3>
                                        <p>Try adjusting your search terms or check back later for new content.</p>
                                    </div>
                                )}
                            </>
                        )}

                        {/* Enhanced Pagination */}
                        {!this.state.loading && this.state.totalResults > 0 && (
                            <div className="pagination-enhanced">
                                <button 
                                    type="button" 
                                    disabled={this.state.page_no <= 1} 
                                    onClick={this.handleprev} 
                                    className="enhanced-btn"
                                >
                                    ⬅️ Previous
                                </button>

                                <div className="page-info">
                                    Page {this.state.page_no} of {totalPages}
                                </div>

                                <button 
                                    type="button" 
                                    disabled={this.state.page_no >= totalPages} 
                                    onClick={this.handlenext} 
                                    className="enhanced-btn"
                                >
                                    Next ➡️
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            </>
        )
    }
}

export default News
