import React, { Component } from 'react'

export class NewsItem extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showAISummary: false,
            showAIAnalysis: false,
            showSentiment: false,
            isBookmarked: false,
            readingTime: 0,
            showFullDescription: false,
            showShareMenu: false,
            isReading: false,
            showKeywords: false
        };
    }

    componentDidMount() {
        this.calculateReadingTime();
        this.checkBookmarkStatus();
    }

    // Calculate estimated reading time
    calculateReadingTime = () => {
        const text = `${this.props.title} ${this.props.desc || ''}`;
        const wordsPerMinute = 200;
        const words = text.split(' ').length;
        const time = Math.ceil(words / wordsPerMinute);
        this.setState({ readingTime: time });
    }

    // Check if article is bookmarked
    checkBookmarkStatus = () => {
        const bookmarks = JSON.parse(localStorage.getItem('bookmarkedArticles') || '[]');
        const isBookmarked = bookmarks.some(bookmark => bookmark.url === this.props.url);
        this.setState({ isBookmarked });
    }

    // Format date
    formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    }

    // Truncate text
    truncateText = (text, maxLength) => {
        if (!text) return 'No description available';
        return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
    }

    // Toggle AI Summary
    toggleAISummary = () => {
        if (!this.props.aiSummary && !this.props.loadingSummary) {
            this.props.onGenerateSummary();
        }
        this.setState({ showAISummary: !this.state.showAISummary });
    }

    // Toggle AI Analysis
    toggleAIAnalysis = () => {
        if (!this.props.aiAnalysis) {
            this.props.onGenerateAnalysis();
        }
        this.setState({ showAIAnalysis: !this.state.showAIAnalysis });
    }

    // Toggle Sentiment Display
    toggleSentiment = () => {
        this.setState({ showSentiment: !this.state.showSentiment });
    }

    // Get sentiment color and emoji
    getSentimentDisplay = (sentiment) => {
        if (!sentiment) return { color: '#gray', emoji: '🤔', text: 'Analyzing...' };

        if (sentiment.includes('POSITIVE')) {
            return { color: '#10B981', emoji: '😊', text: 'Positive' };
        } else if (sentiment.includes('NEGATIVE')) {
            return { color: '#EF4444', emoji: '😟', text: 'Negative' };
        } else {
            return { color: '#6B7280', emoji: '😐', text: 'Neutral' };
        }
    }

    // Bookmark functionality
    toggleBookmark = () => {
        const bookmarks = JSON.parse(localStorage.getItem('bookmarkedArticles') || '[]');
        const articleData = {
            title: this.props.title,
            url: this.props.url,
            urlToImage: this.props.urlToImage,
            source: this.props.source,
            publishedAt: this.props.publishedAt,
            bookmarkedAt: new Date().toISOString()
        };

        if (this.state.isBookmarked) {
            // Remove bookmark
            const updatedBookmarks = bookmarks.filter(bookmark => bookmark.url !== this.props.url);
            localStorage.setItem('bookmarkedArticles', JSON.stringify(updatedBookmarks));
            this.setState({ isBookmarked: false });
        } else {
            // Add bookmark
            bookmarks.push(articleData);
            localStorage.setItem('bookmarkedArticles', JSON.stringify(bookmarks));
            this.setState({ isBookmarked: true });
        }
    }

    // Share functionality
    shareArticle = (platform) => {
        const url = encodeURIComponent(this.props.url);
        const title = encodeURIComponent(this.props.title);

        const shareUrls = {
            twitter: `https://twitter.com/intent/tweet?url=${url}&text=${title}`,
            facebook: `https://www.facebook.com/sharer/sharer.php?u=${url}`,
            linkedin: `https://www.linkedin.com/sharing/share-offsite/?url=${url}`,
            whatsapp: `https://wa.me/?text=${title} ${url}`,
            telegram: `https://t.me/share/url?url=${url}&text=${title}`,
            reddit: `https://reddit.com/submit?url=${url}&title=${title}`
        };

        window.open(shareUrls[platform], '_blank', 'width=600,height=400');
        this.setState({ showShareMenu: false });
    }

    // Copy link
    copyLink = async () => {
        try {
            await navigator.clipboard.writeText(this.props.url);
            alert('Link copied to clipboard!');
            this.setState({ showShareMenu: false });
        } catch (err) {
            console.error('Failed to copy: ', err);
        }
    }

    // Text-to-Speech functionality
    readAloud = () => {
        if (this.state.isReading) {
            window.speechSynthesis.cancel();
            this.setState({ isReading: false });
            return;
        }

        if ('speechSynthesis' in window) {
            const text = `${this.props.title}. ${this.props.desc || ''}`;
            const utterance = new SpeechSynthesisUtterance(text);
            utterance.rate = 0.8;
            utterance.pitch = 1;
            utterance.volume = 0.8;

            utterance.onstart = () => {
                this.setState({ isReading: true });
            };

            utterance.onend = () => {
                this.setState({ isReading: false });
            };

            utterance.onerror = () => {
                this.setState({ isReading: false });
            };

            window.speechSynthesis.speak(utterance);
        } else {
            alert('Text-to-speech not supported in your browser');
        }
    }

    // Extract keywords from AI analysis
    extractKeywords = () => {
        if (!this.props.aiAnalysis) return [];

        // Simple keyword extraction - in a real app, you'd use a proper NLP library
        const keywords = this.props.aiAnalysis
            .split(/[,.\s]+/)
            .filter(word => word.length > 4)
            .slice(0, 5);

        return keywords;
    }

    // Toggle share menu
    toggleShareMenu = () => {
        this.setState({ showShareMenu: !this.state.showShareMenu });
    }

    render() {
        const { 
            title, desc, urlToImage, url, author, publishedAt, source, viewMode = 'grid',
            aiSummary, loadingSummary, aiAnalysis, sentimentAnalysis
        } = this.props;

        const sentimentDisplay = this.getSentimentDisplay(sentimentAnalysis);
        const keywords = this.extractKeywords();

        return (
            <div className={`news-card ${viewMode === 'list' ? 'news-card-list' : ''}`}>
                {/* Image Container */}
                <div className="news-image-container">
                    <img 
                        src={urlToImage} 
                        className="news-image" 
                        alt={title}
                        onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/400x200/667eea/ffffff?text=News+Image';
                        }}
                    />
                    <div className="news-overlay"></div>

                    {/* Enhanced Badge with Sentiment */}
                    <div className="news-badge">
                        📰 {source || 'News'}
                        {sentimentAnalysis && (
                            <span style={{ marginLeft: '5px', fontSize: '12px' }}>
                                {sentimentDisplay.emoji}
                            </span>
                        )}
                    </div>

                    {/* Bookmark Button */}
                    <button 
                        className="bookmark-btn"
                        onClick={this.toggleBookmark}
                        title={this.state.isBookmarked ? 'Remove Bookmark' : 'Add Bookmark'}
                    >
                        {this.state.isBookmarked ? '🔖' : '📑'}
                    </button>

                    {/* Reading Time Badge */}
                    <div className="reading-time-badge">
                        📚 {this.state.readingTime} min
                    </div>
                </div>

                {/* Content */}
                <div className="news-content">
                    <h3 className="news-title">
                        {this.truncateText(title, 80)}
                    </h3>

                    <p className="news-description">
                        {this.state.showFullDescription 
                            ? desc || 'No description available'
                            : this.truncateText(desc, 150)
                        }
                        {desc && desc.length > 150 && (
                            <button 
                                className="read-more-btn"
                                onClick={() => this.setState({ showFullDescription: !this.state.showFullDescription })}
                            >
                                {this.state.showFullDescription ? ' Show Less' : ' Read More'}
                            </button>
                        )}
                    </p>

                    {/* Keywords Section */}
                    {keywords.length > 0 && (
                        <div className="keywords-section">
                            <button 
                                className="keywords-toggle"
                                onClick={() => this.setState({ showKeywords: !this.state.showKeywords })}
                            >
                                🏷️ Keywords ({keywords.length})
                            </button>
                            {this.state.showKeywords && (
                                <div className="keywords-list">
                                    {keywords.map((keyword, index) => (
                                        <span key={index} className="keyword-tag">
                                            {keyword}
                                        </span>
                                    ))}
                                </div>
                            )}
                        </div>
                    )}

                    {/* AI Features Section */}
                    <div className="ai-features-section">
                        {/* AI Summary */}
                        <div className="ai-feature">
                            <button 
                                className={`ai-toggle-btn ${this.state.showAISummary ? 'active' : ''}`}
                                onClick={this.toggleAISummary}
                                disabled={loadingSummary}
                            >
                                🤖 {loadingSummary ? 'Generating...' : 'AI Summary'}
                                {this.state.showAISummary ? ' ▲' : ' ▼'}
                            </button>

                            {this.state.showAISummary && (
                                <div className="ai-content">
                                    {loadingSummary ? (
                                        <div className="ai-loading">
                                            <div className="loading-spinner"></div>
                                            ⏳ Generating AI summary...
                                        </div>
                                    ) : aiSummary ? (
                                        <div className="ai-summary">
                                            <strong>📝 Summary:</strong> {aiSummary}
                                        </div>
                                    ) : (
                                        <div className="ai-placeholder">Click to generate AI summary</div>
                                    )}
                                </div>
                            )}
                        </div>

                        {/* Sentiment Analysis */}
                        {sentimentAnalysis && (
                            <div className="ai-feature">
                                <button 
                                    className={`ai-toggle-btn sentiment-btn ${this.state.showSentiment ? 'active' : ''}`}
                                    onClick={this.toggleSentiment}
                                    style={{ color: sentimentDisplay.color }}
                                >
                                    {sentimentDisplay.emoji} Sentiment: {sentimentDisplay.text}
                                    {this.state.showSentiment ? ' ▲' : ' ▼'}
                                </button>

                                {this.state.showSentiment && (
                                    <div className="ai-content sentiment-details">
                                        <strong>📊 Sentiment Analysis:</strong> {sentimentAnalysis}
                                    </div>
                                )}
                            </div>
                        )}

                        {/* AI Analysis */}
                        <div className="ai-feature">
                            <button 
                                className={`ai-toggle-btn ${this.state.showAIAnalysis ? 'active' : ''}`}
                                onClick={this.toggleAIAnalysis}
                            >
                                📊 AI Analysis
                                {this.state.showAIAnalysis ? ' ▲' : ' ▼'}
                            </button>

                            {this.state.showAIAnalysis && (
                                <div className="ai-content">
                                    {aiAnalysis ? (
                                        <div className="ai-analysis">
                                            <strong>🔍 Analysis:</strong> {aiAnalysis}
                                        </div>
                                    ) : (
                                        <div className="ai-loading">
                                            <div className="loading-spinner"></div>
                                            ⏳ Generating analysis...
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Meta Information */}
                    <div className="news-meta">
                        <div className="news-author">
                            <span>👤</span>
                            <span>{author || 'Unknown Author'}</span>
                        </div>
                        <div className="news-date">
                            <span>📅</span>
                            <span>{this.formatDate(publishedAt)}</span>
                        </div>
                        <div className="news-source">
                            <span>📰</span>
                            <span>{source}</span>
                        </div>
                    </div>

                    {/* Enhanced Actions */}
                    <div className="news-actions">
                        <div className="primary-actions">
                            <a 
                                href={url} 
                                target="_blank" 
                                rel="noopener noreferrer" 
                                className="news-btn primary"
                            >
                                📖 Read Full Article
                            </a>

                            <button 
                                className={`news-btn secondary ${this.state.isReading ? 'active' : ''}`}
                                onClick={this.readAloud}
                                title={this.state.isReading ? 'Stop Reading' : 'Read Aloud'}
                            >
                                {this.state.isReading ? '⏹️ Stop' : '🔊 Listen'}
                            </button>
                        </div>

                        <div className="share-actions">
                            <button 
                                className="news-btn share-btn"
                                onClick={this.toggleShareMenu}
                                title="Share Article"
                            >
                                📤 Share
                            </button>

                            {/* Share Menu */}
                            {this.state.showShareMenu && (
                                <div className="share-menu">
                                    <button 
                                        className="share-option twitter" 
                                        onClick={() => this.shareArticle('twitter')}
                                        title="Share on Twitter"
                                    >
                                        🐦 Twitter
                                    </button>
                                    <button 
                                        className="share-option facebook" 
                                        onClick={() => this.shareArticle('facebook')}
                                        title="Share on Facebook"
                                    >
                                        📘 Facebook
                                    </button>
                                    <button 
                                        className="share-option linkedin" 
                                        onClick={() => this.shareArticle('linkedin')}
                                        title="Share on LinkedIn"
                                    >
                                        💼 LinkedIn
                                    </button>
                                    <button 
                                        className="share-option whatsapp" 
                                        onClick={() => this.shareArticle('whatsapp')}
                                        title="Share on WhatsApp"
                                    >
                                        💬 WhatsApp
                                    </button>
                                    <button 
                                        className="share-option telegram" 
                                        onClick={() => this.shareArticle('telegram')}
                                        title="Share on Telegram"
                                    >
                                        ✈️ Telegram
                                    </button>
                                    <button 
                                        className="share-option reddit" 
                                        onClick={() => this.shareArticle('reddit')}
                                        title="Share on Reddit"
                                    >
                                        🔶 Reddit
                                    </button>
                                    <button 
                                        className="share-option copy" 
                                        onClick={this.copyLink}
                                        title="Copy Link"
                                    >
                                        📋 Copy Link
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* AI Confidence Score */}
                    {(aiSummary || aiAnalysis) && (
                        <div className="ai-confidence">
                            <span className="confidence-label">🎯 AI Confidence:</span>
                            <div className="confidence-bar">
                                <div 
                                    className="confidence-fill"
                                    style={{ width: '85%' }}
                                ></div>
                            </div>
                            <span className="confidence-score">85%</span>
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

export default NewsItem
