import React, { useState, useEffect } from 'react';
import { ChevronLeft, Send, Image, Calendar, MapPin, User, FileText, Tag, CheckCircle, AlertCircle } from 'lucide-react';

const PostNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    author: '',
    category: '',
    location: '',
    imageUrl: '',
    tags: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [localNews, setLocalNews] = useState([]);
  const [errors, setErrors] = useState({});

  // Load existing local news on component mount
  useEffect(() => {
    try {
      const savedNews = JSON.parse(localStorage?.getItem('localNews') || '[]');
      setLocalNews(savedNews);
    } catch (error) {
      console.error('Error loading news:', error);
      setLocalNews([]);
    }
  }, []);

  const categories = [
    'Breaking News',
    'Politics', 
    'Sports',
    'Entertainment',
    'Technology',
    'Health',
    'Business',
    'Education',
    'Weather',
    'Community',
    'Crime',
    'Traffic'
  ];

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    if (!formData.content.trim()) {
      newErrors.content = 'Content is required';
    }
    if (!formData.author.trim()) {
      newErrors.author = 'Author name is required';
    }
    if (!formData.category) {
      newErrors.category = 'Category is required';
    }
    if (formData.imageUrl && !isValidUrl(formData.imageUrl)) {
      newErrors.imageUrl = 'Please enter a valid URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e?.preventDefault?.();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // Create news object
      const newsArticle = {
        id: Date.now().toString(),
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        publishedAt: new Date().toISOString(),
        source: 'Local News',
        isLocal: true
      };

      // Get existing local news
      const existingNews = JSON.parse(localStorage?.getItem('localNews') || '[]');

      // Add new article to the beginning
      const updatedNews = [newsArticle, ...existingNews];

      // Save to localStorage
      if (typeof localStorage !== 'undefined') {
        localStorage.setItem('localNews', JSON.stringify(updatedNews));
      }

      // Update state
      setLocalNews(updatedNews);

      // Show success message
      setSubmitSuccess(true);

      // Reset form
      setFormData({
        title: '',
        content: '',
        author: '',
        category: '',
        location: '',
        imageUrl: '',
        tags: ''
      });

      // Hide success message after 4 seconds
      setTimeout(() => setSubmitSuccess(false), 4000);

    } catch (error) {
      console.error('Error saving news:', error);
      alert('Error saving news. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <>
      <style jsx>{`
        .gradient-bg {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        }

        .card-shadow {
          box-shadow: 0 20px 40px rgba(0,0,0,0.1), 0 8px 16px rgba(0,0,0,0.06);
        }

        .input-focus {
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }

        .input-focus:focus {
          transform: translateY(-1px);
          box-shadow: 0 10px 20px rgba(102, 126, 234, 0.1);
        }

        .btn-primary {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          box-shadow: 0 8px 16px rgba(102, 126, 234, 0.3);
          transition: all 0.3s ease;
        }

        .btn-primary:hover {
          transform: translateY(-2px);
          box-shadow: 0 12px 24px rgba(102, 126, 234, 0.4);
        }

        .btn-primary:active {
          transform: translateY(0);
        }

        .success-animation {
          animation: slideInFromTop 0.5s ease-out;
        }

        @keyframes slideInFromTop {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .news-item {
          transition: all 0.3s ease;
          border-left: 4px solid transparent;
        }

        .news-item:hover {
          transform: translateX(4px);
          border-left-color: #667eea;
        }

        .floating-label {
          transition: all 0.3s ease;
        }

        .form-group:focus-within .floating-label {
          color: #667eea;
          transform: scale(0.95);
        }
      `}</style>

      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <div className="gradient-bg shadow-lg">
          <div className="max-w-6xl mx-auto px-4 py-6">
            <div className="flex items-center gap-4">
              <button 
                onClick={goBack}
                className="p-3 hover:bg-white hover:bg-opacity-20 rounded-xl transition-all duration-300 text-white"
              >
                <ChevronLeft className="w-6 h-6" />
              </button>
              <div className="text-white">
                <h1 className="text-3xl font-bold">Post Local News</h1>
                <p className="text-purple-100 mt-1">Share breaking news from your community</p>
              </div>
            </div>
          </div>
        </div>

        <div className="max-w-6xl mx-auto px-4 py-8">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Main Form */}
            <div className="lg:col-span-2">
              <div className="bg-white card-shadow rounded-3xl p-8">
                <div className="space-y-8">
                  {/* Title */}
                  <div className="form-group">
                    <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <FileText className="w-4 h-4" />
                      News Title *
                    </label>
                    <input
                      type="text"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      placeholder="Enter compelling news headline..."
                      className={`input-focus w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-purple-100 transition-all ${
                        errors.title ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-purple-400'
                      }`}
                      required
                    />
                    {errors.title && (
                      <p className="text-red-500 text-sm mt-2 flex items-center gap-1">
                        <AlertCircle className="w-4 h-4" />
                        {errors.title}
                      </p>
                    )}
                  </div>

                  {/* Content */}
                  <div className="form-group">
                    <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <FileText className="w-4 h-4" />
                      News Content *
                    </label>
                    <textarea
                      name="content"
                      value={formData.content}
                      onChange={handleInputChange}
                      placeholder="Write your news article content here... Include who, what, when, where, and why."
                      rows="10"
                      className={`input-focus w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-purple-100 transition-all resize-none ${
                        errors.content ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-purple-400'
                      }`}
                      required
                    />
                    {errors.content && (
                      <p className="text-red-500 text-sm mt-2 flex items-center gap-1">
                        <AlertCircle className="w-4 h-4" />
                        {errors.content}
                      </p>
                    )}
                  </div>

                  {/* Author and Category Row */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="form-group">
                      <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <User className="w-4 h-4" />
                        Author Name *
                      </label>
                      <input
                        type="text"
                        name="author"
                        value={formData.author}
                        onChange={handleInputChange}
                        placeholder="Your full name"
                        className={`input-focus w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-purple-100 transition-all ${
                          errors.author ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-purple-400'
                        }`}
                        required
                      />
                      {errors.author && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1">
                          <AlertCircle className="w-4 h-4" />
                          {errors.author}
                        </p>
                      )}
                    </div>

                    <div className="form-group">
                      <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <Tag className="w-4 h-4" />
                        Category *
                      </label>
                      <select
                        name="category"
                        value={formData.category}
                        onChange={handleInputChange}
                        className={`input-focus w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-purple-100 transition-all ${
                          errors.category ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-purple-400'
                        }`}
                        required
                      >
                        <option value="">Select category</option>
                        {categories.map(cat => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                      {errors.category && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1">
                          <AlertCircle className="w-4 h-4" />
                          {errors.category}
                        </p>
                      )}
                    </div>
                  </div>

                  {/* Location and Image URL Row */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="form-group">
                      <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <MapPin className="w-4 h-4" />
                        Location
                      </label>
                      <input
                        type="text"
                        name="location"
                        value={formData.location}
                        onChange={handleInputChange}
                        placeholder="City, State/Country"
                        className="input-focus w-full px-6 py-4 border-2 border-gray-200 rounded-2xl focus:border-purple-400 focus:ring-4 focus:ring-purple-100 transition-all"
                      />
                    </div>

                    <div className="form-group">
                      <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                        <Image className="w-4 h-4" />
                        Image URL
                      </label>
                      <input
                        type="url"
                        name="imageUrl"
                        value={formData.imageUrl}
                        onChange={handleInputChange}
                        placeholder="https://example.com/image.jpg"
                        className={`input-focus w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-purple-100 transition-all ${
                          errors.imageUrl ? 'border-red-400 focus:border-red-500' : 'border-gray-200 focus:border-purple-400'
                        }`}
                      />
                      {errors.imageUrl && (
                        <p className="text-red-500 text-sm mt-2 flex items-center gap-1">
                          <AlertCircle className="w-4 h-4" />
                          {errors.imageUrl}
                        </p>
                      )}
                    </div>
                  </div>

                  {/* Tags */}
                  <div className="form-group">
                    <label className="floating-label flex items-center gap-2 text-sm font-semibold text-gray-700 mb-3">
                      <Tag className="w-4 h-4" />
                      Tags
                    </label>
                    <input
                      type="text"
                      name="tags"
                      value={formData.tags}
                      onChange={handleInputChange}
                      placeholder="breaking, local, community, urgent"
                      className="input-focus w-full px-6 py-4 border-2 border-gray-200 rounded-2xl focus:border-purple-400 focus:ring-4 focus:ring-purple-100 transition-all"
                    />
                    <p className="text-sm text-gray-500 mt-2">Separate tags with commas for better categorization</p>
                  </div>

                  {/* Submit Button */}
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="btn-primary w-full text-white py-5 px-8 rounded-2xl font-bold text-lg disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-3"
                  >
                    {isSubmitting ? (
                      <>
                        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                        Publishing Article...
                      </>
                    ) : (
                      <>
                        <Send className="w-6 h-6" />
                        Publish News Article
                      </>
                    )}
                  </button>
                </div>

                {/* Success Message */}
                {submitSuccess && (
                  <div className="success-animation mt-8 p-6 bg-gradient-to-r from-green-400 to-green-500 text-white rounded-2xl card-shadow">
                    <div className="flex items-center gap-3">
                      <CheckCircle className="w-6 h-6" />
                      <div>
                        <p className="font-bold text-lg">Article Published Successfully! 🎉</p>
                        <p className="text-green-100">Your local news has been added to the community feed.</p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Sidebar */}
            <div className="space-y-6">
              {/* Guidelines */}
              <div className="bg-white card-shadow rounded-3xl p-6">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                  <FileText className="w-5 h-5 text-purple-600" />
                  Publishing Guidelines
                </h3>
                <div className="space-y-4 text-sm text-gray-600">
                  <div className="flex items-start gap-3 p-3 bg-purple-50 rounded-lg">
                    <div className="w-2 h-2 bg-purple-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong>Clear Headlines:</strong> Write compelling, accurate headlines that capture your story's essence</p>
                  </div>
                  <div className="flex items-start gap-3 p-3 bg-blue-50 rounded-lg">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong>Complete Context:</strong> Include who, what, when, where, and why in your content</p>
                  </div>
                  <div className="flex items-start gap-3 p-3 bg-green-50 rounded-lg">
                    <div className="w-2 h-2 bg-green-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong>Proper Categorization:</strong> Choose appropriate categories and add relevant tags</p>
                  </div>
                  <div className="flex items-start gap-3 p-3 bg-orange-50 rounded-lg">
                    <div className="w-2 h-2 bg-orange-500 rounded-full mt-2 flex-shrink-0"></div>
                    <p><strong>Fact Verification:</strong> Verify information and cite sources when possible</p>
                  </div>
                </div>
              </div>

              {/* Recent Local News */}
              <div className="bg-white card-shadow rounded-3xl p-6">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                  <Calendar className="w-5 h-5 text-purple-600" />
                  Recent Local News
                </h3>
                {localNews.length > 0 ? (
                  <div className="space-y-4">
                    {localNews.slice(0, 3).map(news => (
                      <div key={news.id} className="news-item p-4 bg-gray-50 rounded-xl cursor-pointer">
                        <h4 className="font-semibold text-sm text-gray-900 mb-2 line-clamp-2">{news.title}</h4>
                        <div className="flex items-center gap-2 text-xs text-gray-500">
                          <span className="px-2 py-1 bg-purple-100 text-purple-700 rounded-full font-medium">
                            {news.category}
                          </span>
                          <span>•</span>
                          <span>{new Date(news.publishedAt).toLocaleDateString()}</span>
                        </div>
                      </div>
                    ))}
                    <div className="text-center pt-4 border-t border-gray-100">
                      <p className="text-xs text-gray-500">
                        <strong>{localNews.length}</strong> total local {localNews.length === 1 ? 'article' : 'articles'} published
                      </p>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
                      <FileText className="w-8 h-8 text-purple-500" />
                    </div>
                    <p className="text-gray-500 text-sm">No local news articles yet.</p>
                    <p className="text-gray-700 font-semibold text-sm mt-1">Be the first to post!</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default PostNews;
